INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (100310, 'Atracurium', 'Lincomycin', 'Moderate', 'Lincosamides (e.g., clindamycin, lincomycin) have demonstrated neuromuscular blocking activity in vitro and may augment the pharmacologic effects of neuromuscular blocking agents. Clinically, the interaction has been reported in isolated cases involving atracurium, pancuronium, pipecuronium, succinylcholine, and tubocurarine.', NULL, 'Patients receiving neuromuscular blockers in conjunction with lincosamide therapy should be monitored closely during and after anesthesia for development of respiratory depression, and life support should be readily available if needed.', NULL, 'Patients receiving neuromuscular blockers in conjunction with lincosamide therapy should be monitored closely during and after anesthesia for development of respiratory depression, and life support should be readily available if needed. Anticholinesterases and calcium may not completely antagonize the neuromuscular blockade induced by lincosamides.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/100424/', '[1] Cerner Multum, Inc. "Australian Product Information." O 0[2] "Product Information. Clindesse (clindamycin topical)." Ther-Rx Corporation, Bridgeton, MO.[3] Jedeikin R, Dolgunski E, Kaplan R, Hoffman S "Prolongation of neuromuscular blocking effect of vecuronium by antibiotics." Anaesthesia 42 (1987): 858-60[4] "Product Information. Cleocin (clindamycin)." Pharmacia and Upjohn, Kalamazoo, MI.[5] Fogdall RP, Miller RD "Antagonism of d-tubocurarine- and pancuronium-induced neuromuscular blockades by pyridostigmine in man." Anesthesiology 39 (1973): 504-9[6] Avery