INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (100351, 'Esomeprazole', 'Liothyronine', 'Moderate', 'Concurrent administration of proton pump inhibitors (PPI) may decrease the oral bioavailability of levothyroxine. Pharmacologic effects of levothyroxine may be reduced. The mechanism of interaction is suspected to be PPI induced hypochlorhydria leading to reduced levothyroxine absorption since gastric acidity is an essential requirement for levothyroxine absorption.', NULL, 'Caution is advised if levothyroxine is used concomitantly with proton pump inhibitors.', NULL, 'Caution is advised if levothyroxine is used concomitantly with proton pump inhibitors. Consider the alteration in gastric pH caused by the PPI. Some authorities recommend separating administration of PPI and levothyroxine by several hours, however there are no studies showing improved absorption when PPIs are administered separately from levothyroxine. If concomitant administration is necessary, consider monitoring TSH level and watching for clinical evidence of reduced levothyroxine effects. Patients should be advised to contact their physician if they experience symptoms of hypothyroidism, such as fatigue, cold intolerance, constipation, unexplained weight gain, depression, joint or muscle pain, thinning hair or hair loss, dry skin, hoarseness, and abnormal menstrual periods.', 'Absorption', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/100465/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Product Information. Synthroid (levothyroxine)." Abbott Pharmaceutical, Abbott Park, IL.[3] Cerner Multum, Inc. "Australian Product Information." O 0[4] Wohlt PD, Zheng L, Gunderson S, Balzar SA, Johnson BD, Fish JT "Recommendations for the use of medications with continuous enteral nutrition." Am J Health Syst Pharm 66 (2009): 1438-67[5] "Product Information. Armour thyroid (thyroid desiccated)." Forest Pharmaceuticals, St. Louis, MO.[6] "Product Information. Synthroid (levothyroxine)." Abbott Pharmaceutical, Abbott Park, IL.', 1767363023);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (100352, 'Esterified estrogens', 'Liothyronine', 'Moderate', 'Estrogens may increase serum thyrotropin concentration, which could be harmful in patients with thyroid cancer receiving thyroxine for thyrotropin suppression or increase dosage requirements in patients with hypothyroidism receiving thyroxine for replacement therapy. Estrogens are known to increase serum thyroid-binding globulin concentration in a dose-dependent manner. Consequently, there may be a reduction in unbound, or free, thyroxine available for hormone activity, which, in turn, leads to an increase in serum thyrotropin concentration. Normally, thyroxine secretion can increase to compensate for this effect, but patients with hypothyroidism lack the mechanism to adapt.', NULL, 'In patients treated with thyroxine, serum thyrotropin should be measured approximately 12 weeks after estrogen therapy is initiated, changed or discontinued, and the thyroxine dosage adjusted accordingly.', NULL, 'In patients treated with thyroxine, serum thyrotropin should be measured approximately 12 weeks after estrogen therapy is initiated, changed or discontinued, and the thyroxine dosage adjusted accordingly. Patients should be advised to contact their physician if clinical manifestations of hypothyroidism occur, such as fatigue, cold intolerance, constipation, unexplained weight gain, depression, joint or muscle pain, thinning hair or hair loss, dry skin, hoarseness, and abnormal menstrual periods.', 'Distribution', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/100466/', '[1] Arafah BM "Increased need for thyroxine in women with hypothyroidism during e