INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (100369, 'Imatinib', 'Liothyronine', 'Moderate', 'Imatinib has been reported to induce clinical hypothyroidism in patients receiving levothyroxine. The exact mechanism is unknown but may involve stimulation of the non-deiodination clearance of T4 and T3 by imatinib.', NULL, 'Monitoring of thyroid function including laboratory serum TSH concentrations should be performed before, during, and after any dosage change or discontinuation of imatinib therapy, and the levothyroxine dosage adjusted accordingly.', NULL, 'Monitoring of thyroid function including laboratory serum TSH concentrations should be performed before, during, and after any dosage change or discontinuation of imatinib therapy, and the levothyroxine dosage adjusted accordingly. The authors of the study cited above suggest a minimum 2-fold increase in levothyroxine therapy before initiation of imatinib treatment and close monitoring of thyroid function during treatment.', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/100483/', '[1] de Groot JW, Zonnenberg BA, Plukker JT, van Der Graaf WT, Links TP "Imatinib induces hypothyroidism in patients receiving levothyroxine." Clin Pharmacol Ther 78 (2005): 433-8[2] Wohlt PD, Zheng L, Gunderson S, Balzar SA, Johnson BD, Fish JT "Recommendations for the use of medications with continuous enteral nutrition." Am J Health Syst Pharm 66 (2009): 1438-67[3] "Product Information. Armour thyroid (thyroid desiccated)." Forest Pharmaceuticals, St. Louis, MO.[4] "Product Information. Synthroid (levothyroxine)." Abbott Pharmaceutical, Abbott Park, IL.[5] Cerner Multum, Inc. "Australian Product Information." O 0[6] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[7] "Product Information. Gleevec (imatinib mesylate)." Novartis Pharmaceuticals, East Hanover, NJ.', 1767363023);
