INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (100436, 'Secobarbital', 'Liothyronine', 'Moderate', 'Barbiturates may decrease exogenous thyroid plasma concentrations resulting in elevated thyroid stimulating hormone (TSH) and exacerbation of hypothyroidism. The mechanism may be induction of hepatic CYP450 enzymes responsible for thyroxine (T4) and triiodothyronine (T3) metabolism. Clinical data are limited.', NULL, 'Clinical monitoring of patient response, including laboratory serum TSH concentrations, is recommended.', NULL, 'Clinical monitoring of patient response, including laboratory serum TSH concentrations, is recommended. Adjustment of thyroid replacement dosage may be indicated when initiating or discontinuing barbiturate therapy.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/100550/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Multum Information Services, Inc. Expert Review Panel"[3] Wohlt PD, Zheng L, Gunderson S, Balzar SA, Johnson BD, Fish JT "Recommendations for the use of medications with continuous enteral nutrition." Am J Health Syst Pharm 66 (2009): 1438-67[4] "Product Information. Armour thyroid (thyroid desiccated)." Forest Pharmaceuticals, St. Louis, MO.[5] "Product Information. Synthroid (levothyroxine)." Abbott Pharmaceutical, Abbott Park, IL.', 1767363023);
