INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (100469, 'Iron', 'Liotrix', 'Moderate', 'INTERVAL: Concurrent administration of iron-containing products may decrease the oral bioavailability and pharmacologic effects of levothyroxine. The exact mechanism of interaction is unknown, but in vitro data suggest it may involve nonspecific adsorption of levothyroxine to iron at acidic pH levels, resulting in an insoluble complex that is poorly absorbed from the gastrointestinal tract.', NULL, 'Some experts recommend separating the times of administration of levothyroxine and iron-containing preparations by at least 2 to 4 hours.', NULL, 'Some experts recommend separating the times of administration of levothyroxine and iron-containing preparations by at least 2 to 4 hours. Monitoring of serum TSH levels is recommended. Patients with gastrointestinal or malabsorption disorders may be at a greater risk of developing clinical or subclinical hypothyroidism due to this interaction.', 'Absorption', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/100583/', '[1] Campbell NR, Hasinoff BB, Stalts H, Rao B, Wong NC "Ferrous sulfate reduces thyroxine efficacy in patients with hypothyroidism." Ann Intern Med 117 (1992): 1010-3[2] Wohlt PD, Zheng L, Gunderson S, Balzar SA, Johnson BD, Fish JT "Recommendations for the use of medications with continuous enteral nutrition." Am J Health Syst Pharm 66 (2009): 1438-67[3] "Product Information. Armour thyroid (thyroid desiccated)." Forest Pharmaceuticals, St. Louis, MO.[4] "Product Information. Synthroid (levothyroxine)." Abbott Pharmaceutical, Abbott Park, IL.', 1767363023);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (100470, 'Carteolol', 'Liotrix', 'Minor', 'Thyroid hormone therapy may reverse decreased hepatic blood flow associated with hypothyroidism. Increased hepatic metabolism and decreased serum levels of some beta-blockers may result. Data are available for propranolol only. No special precautions are necessary. When hypothyroidism is converted to a euthyroid state, a decrease in beta-blocking effectiveness is possible.', NULL, '-', NULL, '-', 'Others', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/100584/', '[1] Aro A, Antila M, Korhonen T, Sundquist H "Pharmacokinetics of propranolol and sotalol in hyperthyroidism." Eur J Clin Pharmacol 21 (1982): 373-7[2] Wells PG, Feely J, Wilkinson GR, Wood AJ "Effect of thyrotoxicosis on liver blood flow and propranolol disposition after long-term dosing." Clin Pharmacol Ther 33 (1983): 603-8[3] Wohlt PD, Zheng L, Gunderson S, Balzar SA, Johnson BD, Fish JT "Recommendations for the use of medications with continuous enteral nutrition." Am J Health Syst Pharm 66 (2009): 1438-67[4] "Product Information. Armour thyroid (thyroid desiccated)." Forest Pharmaceuticals, St. Louis, MO.[5] "Product Information. Synthroid (levothyroxine)." Abbott Pharmaceutical, Abbott Park, IL.', 1767363023);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (100471, 'Conjugated estrogens (topical)', 'Liotrix', 'Moderate', 'Estrogens may increase serum thyrotropin concentration, which could be harmful in patients with thyroid cancer receiving thyroxine for thyrotropin suppression or increase dosage requirements in patients with hypothyroidism receiving thyroxine for replacement therapy. Estrogens are known to increase serum thyroid-binding globulin concentration in a dose-dependent manner. Consequently, there may be a reduction in unbound, or free, thyroxine available for hormone activity, which, in turn, leads to an increase in serum thyrotropin concentration. Normally, thyroxine secretion can increase to compensate for this effect, but patients with hypothyroidism lack the mechanism to adapt.', NULL, 'In patients treated with thyroxine, serum thyrotropin should be measured approximately 12 weeks after estrogen therapy is initiated, changed or discontinued, and the thyroxine dosage adjusted accordingly.', NULL, 'In patients treated with thyroxine, serum thyrotropin should be measured approximately 12 weeks after estrogen therapy is initiated, changed or discontinued, and the thyroxine dosage adjusted accordingly. Patients should be advised to contact their physician if clinical manifestations of hypothyroidism occur, such as fatigue, cold intolerance, constipation, unexplained weight gain, depression, joint or muscle pain, thinning hair or hair loss, dry skin, hoarseness, and abnormal menstrual periods.', 'Distribution', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/100585/', '[1] Arafah BM "Increased need for thyroxine in women with hypothyroidism dur