INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (100537, 'Pentobarbital', 'Liotrix', 'Moderate', 'Barbiturates may decrease exogenous thyroid plasma concentrations resulting in elevated thyroid stimulating hormone (TSH) and exacerbation of hypothyroidism. The mechanism may be induction of hepatic CYP450 enzymes responsible for thyroxine (T4) and triiodothyronine (T3) metabolism. Clinical data are limited.', NULL, 'Clinical monitoring of patient response, including laboratory serum TSH concentrations, is recommended.', NULL, 'Clinical monitoring of patient response, including laboratory serum TSH concentrations, is recommended. Adjustment of thyroid replacement dosage may be indicated when initiating or discontinuing barbiturate therapy.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/100651/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Multum Information Services, Inc. Expert Review Panel"[3] Wohlt PD, Zheng L, Gunderson S, Balzar SA, Johnson BD, Fish JT "Recommendations for the use of medications with continuous enteral nutrition." Am J Health Syst Pharm 66 (2009): 1438-67[4] "Product Information. Armour thyroid (thyroid desiccated)." Forest Pharmaceuticals, St. Louis, MO.[5] "Product Information. Synthroid (levothyroxine)." Abbott Pharmaceutical, Abbott Park, IL.', 1767365206);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (100538, 'Phenytoin', 'Liotrix', 'Moderate', 'Carbamazepine and phenytoin may decrease exogenous thyroid plasma concentrations resulting in elevated thyroid stimulating hormone (TSH) and possible exacerbation of hypothyroidism. The mechanism may be induction of hepatic CYP450 enzymes responsible for thyroxine (T4) and triiodothyronine (T3) metabolism. In addition, T3 and T4 may be displaced from thyroxine binding globulin by phenytoin. Clinical data are limited.', NULL, 'Clinical monitoring of patient response, including laboratory serum TSH concentrations, is recommended.', NULL, 'Clinical monitoring of patient response, including laboratory serum TSH concentrations, is recommended. Adjustment of the thyroid replacement dosage may be indicated when initiating or discontinuing phenytoin or carbamazepine therapy.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/100652/', '[1] "Multum Information Services, Inc. Expert Review Panel"[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] Cerner Multum, Inc. "Australian Product Information." O 0[4] Ozuna J, Friel P "Effect of enteral tube feeding on serum phenytoin levels." J Neurosurg Nurs 16 (1984): 289-91[5] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[6] Fleisher D, Sheth N, Kou JH "Phenytoin interaction with enteral feedings administered through nasogastric tubes." JPEN J Parenter Enteral Nutr 14 (1990): 513-6[7] Krueger KA, Garnett WR, Comstock TJ, Fitzsimmons WE, Karnes HT, Pellock JM "Effect of two administration schedules of an enteral nutrient formula on phenytoin bioavailability." Epilepsia 28 (