INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (100601, 'Aminophylline', 'Ritonavir', 'Moderate', 'The coadministration with ritonavir may decrease the plasma concentrations of theophylline. The proposed mechanism is ritonavir induction of CYP450 1A2, the isoenzyme responsible for the metabolic clearance of theophylline.', NULL, 'During coadministration with ritonavir, the possibility of a diminished therapeutic response to theophylline should be considered.', NULL, 'During coadministration with ritonavir, the possibility of a diminished therapeutic response to theophylline should be considered. Dosage adjustment as well as clinical and laboratory monitoring may be appropriate whenever ritonavir is added to or withdrawn from therapy.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/100715/', '[1] "Product Information. Norvir (ritonavir)." Abbott Pharmaceutical, Abbott Park, IL.[2] Hsu A, Granneman GR, Witt G, Cavanaugh JH, Leonard J "Assessment of multiple doses of ritonavir on the pharmacokinetics of theophylline." Int Conf AIDS 11 (1996): 89(ab.no.mo.b.1200)[3] "Product Information. Kaletra (lopinavir-ritonavir)" Abbott Pharmaceutical, Abbott Park, IL.[4] "Product Information. Norvir (ritonavir)." Abbott Pharmaceutical, Abbott Park, IL.', 1767365206);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (100602, 'Amisulpride', 'Lopinavir', 'Major', 'Amisulpride can cause dose- and concentration-dependent prolongation of the QT interval. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', NULL, 'Caution and close clinical monitoring (e.', NULL, 'Caution and close clinical monitoring (e.g., electrocardiogram, serum electrolytes) are recommended if amisulpride is used in combination with other drugs that can prolong the QT interval. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/100716/', '[1] "Product Information. Barhemsys (amisulpride)." Acacia Pharma, Inc, Indianapolis, IN.[2] "Product Information. Kaletra (lopinavir-ritonavir)" Abbott Pharmaceutical, Abbott Park, IL.[3] "Product Information. Norvir (ritonavir)." Abbott Pharmaceutical, Abbott Park, IL.', 1767365206);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (100603, 'Amoxapine', 'Ritonavir', 'Moderate', 'Coadministration with ritonavir may increase the plasma concentrations of drugs that are substrates of the CYP450 2D6 isoenzyme. The mechanism is decreased clearance due to competitive inhibition of CYP450 2D6 activity by ritonavir.', NULL, 'Caution is advised if ritonavir must be used concurrently with medications that undergo metabolism by CYP450 2D6, particularly those with a narrow therapeutic range.', NULL, 'Caution is advised if ritonavir must be used concurrently with medications that undergo metabolism by CYP450 2D6, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever ritonavir is added to or withdrawn from therapy.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/100717/', '[1] "Product Information. Norvir (ritonavir)." Abbott Pharmaceutical, Abbott Park, IL.[2] "Product Information. Norvir (ritonavir)." Abbott Pharmaceutical, Abbott Park, IL.[3] "Product Information. Viekira Pak (dasabuvir/ombitasvir/paritaprevir/ritonavir)." AbbVie US LLC, North Chicago, IL.', 1767365206);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (100604, 'Amprenavir', 'Lopinavir', 'Major', 'Coadministration with amprenavir may decrease the plasma concentrations of lopinavir, even in the presence of low-dose ritonavir as a pharmacokinetic booster. The proposed mechanism is amprenavir induction of lopinavir metabolism via CYP450 3A4.', NULL, 'A dosage increase is recommended for lopinavir-ritonavir when used in combination with amprenavir.', NULL, 'A dosage increase is recommended for lopinavir-ritonavir when used in combination with amprenavir. For adults, the dosage should be increased to 500 mg-125 mg (two 200 mg-50 mg tablets and one 100 mg-25 mg tablet) twice daily for the tablets and 533 mg-133 mg (6.5 mL) twice daily for the oral solution. Lopinavir-ritonavir should not be administered as a once daily regimen in combination with amprenavir. For pediatric patients 6 months to 18 years of age, the dosage should be increased to 300 mg-75 mg/m2 twice daily for the oral solution in both treatment-naive and treatment-experienced patients, not to exceed the recommended adult dosage of 533 mg-133 mg (6.5 mL) twice daily. If weight-based dosing is preferred, the recommended dosage is 13 mg-3.25 mg/kg given twice daily for patients under 15 kg and 11 mg-2.75 mg/kg given twice daily for patients 15 kg to 45 kg. Pediatric patients weighing more than 45 kg should receive the adult dosage of lopinavir-ritonavir. Please consult the manufacturer''s product labeling for pediatric dosing of the tablet formulation. Lopinavir-ritonavir should not be administered in combination with amprenavir in patients under 6 months of age due to the lack of clinical data.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/100718/', '[1] Canadian Pharmacists Association "e-CPS. Available from: URL: http://www.pharmacists.ca/function/Subscriptions/ecps.cfm?link=eCPS_quikLink."[2] "Product Information. Kaletra (lopinavir-ritonavir)" Abbott Pharmaceutical, Abbott Park, IL.[3] "Product Information. Agenerase (amprenavir)." Glaxo Wellcome, Research Triangle Pk, NC.[4] "Product Information. Agenerase (amprenavir)." Glaxo Wellcome, Research Triangle Pk, NC.[5] Demarles D, Gillotin C, Bonaventure-Paci S, Vincent I, Fosse S, Taburet AM "Single-dose pharmacokinetics of amprenavir coadministered with grapefruit juice." Antimicrob Agents Chemother 46 (2002): 1589-1590[6] "Product Information. Kaletra (lopinavir-ritonavir)" Abbott Pharmaceutical, Abbott Park, IL.[7] "Product Information. Norvir (ritonavir)." Abbott Pharmaceutical, Abbott Park, IL.', 1767365206);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (100605, 'Anagrelide', 'Lopinavir', 'Major', 'Anagrelide can cause dose-dependent prolongation of the QT interval. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', NULL, 'Coadministration of anagrelide with other drugs that can prolong the QT interval should generally be avoided.', NULL, 'Coadministration of anagrelide with other drugs that can prolong the QT interval should generally be avoided. Patients receiving anagrelide should have a cardiovascular examination, including an ECG, prior to initiation of treatment and monitored for cardiovascular effects during treatment. Hypokalemia or hypomagnesemia must be corrected prior to therapy and electrolytes monitored periodically during therapy.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/100719/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] "Product Information. Agrylin (anagrelide)." Roberts Pharmaceutical Corporation, Eatontown, NJ.[4] Canadian Pharmacists Association "e-CPS. Available from: URL: http://www.pharmacists.ca/function/Subscriptions/ecps.cfm?link=eCPS_quikLink."[5] Mendelson J, Jones RT, Upton R, Jacob P 3rd "Methamphetamine and ethanol interactions in humans." Clin Pharmacol Ther 57 (1995): 559-68[6] Jiao X, Velez S, Ringstad J, Eyma V, Miller D, Bleiberg M "Myocardial infarction associated with Adderall XR and alcohol use in a young man." J Am Board Fam Med 22 (2009): 197-201', 1767365206);
