INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (100609, 'Armodafinil', 'Lopinavir', 'Moderate', 'Coadministration with modafinil (the racemate) or armodafinil (the R-enantiomer) may decrease the plasma concentrations of protease inhibitors (PIs). The mechanism involves induction of intestinal CYP450 3A4-mediated first-pass metabolism by modafinil and armodafinil, with possible secondary induction of hepatic 3A4 metabolism. Significant intestinal metabolism appears to occur with indinavir and saquinavir but has not been studied with other PIs. Conversely, PIs may increase the plasma concentrations of modafinil via inhibition of hepatic CYP450 3A4 metabolism.', NULL, 'Given the risk of reduced viral susceptibility and resistance development associated with decreased or subtherapeutic antiretroviral drug levels, caution is advised if modafinil or armodafinil must be used in patients treated with PIs, particularly if they are using only one PI in their antiretroviral regimen.', NULL, 'Given the risk of reduced viral susceptibility and resistance development associated with decreased or subtherapeutic antiretroviral drug levels, caution is advised if modafinil or armodafinil must be used in patients treated with PIs, particularly if they are using only one PI in their antiretroviral regimen. Clinical monitoring for altered effects of both modafinil and the PI may be appropriate following addition or withdrawal of one or the other drug.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/100723/', '[1] "Product Information. Nuvigil (armodafinil)." Cephalon Inc, West Chester, PA.[2] Sommadossi JP "HIV protease inhibitors: pharmacologic and metabolic distinctions." AIDS 13 (1999): s29-40[3] Durant J, Clevenbergh P, Garraffo R, Halfon P, Icard S, DelGiudice P, Montagne N, Schapiro JM, Dellamonica P "Importance of protease inhibitor plasma levels in HIV-infected patients treated with genotypic-guided therapy: pharmacological data from the Viradapt Study." Aids 14 (2000): 1333-9[4] Robertson P, Decory HH, Madan A, Parkinson A "In vitro inhibition and induction of human hepatic cytochrome P450 enzymes by modafinil." Drug Metab Dispos 28 (2000): 664-71[5] Barry M, Gibbons S, Back D, Mulcahy F "Protease inhibitors in patients with HIV disease. Clinically important pharmacokinetic considerations." Clin Pharmacokinet 32 (1997): 194-209[6] "Product Information. Provigil (modafinil)." Cephalon, Inc, West Chester, PA.[7] Doherty MM, Charman WN "The mucosa of the small intestine: how clinically relevant as an organ of drug metabolism?" Clin Pharmacokinet 41 (2002): 235-53[8] "Product Information. Provigil (modafinil)." Cephalon, Inc, West Chester, PA.[9] "Product Information. Nuvigil (armodafinil)." Cephalon Inc, West Chester, PA.', 1767365206);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (100610, 'Arsenic trioxide', 'Lopinavir', 'Major', 'Arsenic trioxide can cause QT interval prolongation and complete atrioventricular block. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', NULL, 'If possible, medications that are known to prolong the QT interval should be discontinued prior to initiating therapy with arsenic trioxide and withheld for at least several weeks after completion of therapy.', NULL, 'If possible, medications that are known to prolong the QT interval should be discontinued prior to initiating therapy with arsenic trioxide and withheld for at least several weeks after completion of therapy. Caution is advised if concomitant use cannot be avoided. Patients should have frequent ECGs and be monitored for arrhythmias when QT interval is prolonged. An absolute QT interval exceeding 500 msec will require immediate action to correct concomitant risk factors, if any, as well as a thorough assessment of the need for continued therapy. Patients who develop syncope or arrhythmia should be hospitalized for clinical and laboratory monitoring.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/100724/', '[1] "Product Information. Trisenox (arsenic trioxide)" Cephalon Inc, West Chester, PA.[2] Ohnishi K, Yoshida H, Shigeno K, et al. "Prolongation of the QT interval and ventricular tachycardia in patients treated with arsenic trioxide for acute promyelocytic leukemia." Ann Intern Med 133 (2000): 881-5[3] "Product Information. Kaletra (lopinavir-ritonavir)" Abbott Pharmaceutical, Abbott Park, IL.[4] "Product Information. Norvir (ritonavir)." Abbott Pharmaceutical, Abbott Park, IL.', 1767365206);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (100611, 'Ritonavir', 'Artesunate', 'Moderate', 'Coadministration with ritonavir may decrease the peak plasma concentration (Cmax) and systemic exposure (AUC) of dihydroartemisinin (DHA), an active metabolite of artesunate. The mechanism is unknown. According to the manufacturer, this reduction in pharmacokinetic parameters is also likely to occur with IV artesunate. Reduced efficacy of artesunate may occur.', NULL, 'The potential for diminished pharmacologic effects of artesunate should be considered during coadministration with ritonavir.', NULL, 'The potential for diminished pharmacologic effects of artesunate should be considered during coadministration with ritonavir. Alternative treatments may be required if an interaction is suspected.', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/100725/', '[1] "Product Information. Artesunate (artesunate)." Amivas, Frederick, MD.[2] "Product Information. Kaletra (lopinavir-ritonavir)" Abbott Pharmaceutical, Abbott Park, IL.[3] "Product Information. Norvir (ritonavir)." Abbott Pharmaceutical, Abbott Park, IL.', 1767365206);
