INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (100616, 'Bedaquiline', 'Lopinavir', 'Major', 'Bedaquiline can cause dose-related prolongation of the QT interval. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', NULL, 'Caution is recommended if bedaquiline is used in combination with other drugs that can prolong the QT interval.', NULL, 'Caution is recommended if bedaquiline is used in combination with other drugs that can prolong the QT interval. ECG and serum electrolytes, including potassium, magnesium and calcium, should be monitored before starting bedaquiline therapy and periodically during treatment in accordance with the product labeling. Hypokalemia, hypomagnesemia, and hypocalcemia must be corrected prior to bedaquiline administration.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/100730/', '[1] "Product Information. Sirturo (bedaquiline)." Janssen Pharmaceuticals, Titusville, NJ.[2] "Product Information. Sirturo (bedaquiline)." Janssen Pharmaceuticals, Titusville, NJ.', 1767365206);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (100617, 'Benznidazole', 'Ritonavir', 'Moderate', 'Ritonavir capsules, ritonavir oral solution, lopinavir-ritonavir oral solution, and tipranavir capsules all contain alcohol, which may produce a disulfiram-like reaction when coadministered with drugs that can inhibit aldehyde dehydrogenase (ALDH) such as nitroimidazoles (e.g., metronidazole, tinidazole), nitrofurans (e.g., furazolidone, nifurtimox), and cephalosporins with an N-methylthiotetrazole (NMTT) side chain that structurally resembles disulfiram. Following ingestion of alcohol, inhibition of ALDH results in increased concentrations of acetaldehyde, the accumulation of which can produce an unpleasant physiologic response referred to as the ''disulfiram reaction''. Symptoms include flushing, throbbing in head and neck, throbbing headache, respiratory difficulty, nausea, vomiting, sweating, thirst, chest pain, palpitation, dyspnea, hyperventilation, tachycardia, hypotension, syncope, weakness, vertigo, blurred vision, and confusion. Severe reactions may result in respiratory depression, cardiovascular collapse, arrhythmia, myocardial infarction, acute congestive heart failure, unconsciousness, convulsions, and death. The interaction is well established for disulfiram.', NULL, 'Until further information is available, use of ritonavir capsules, ritonavir oral solution, lopinavir-ritonavir oral solution, or tipranavir capsules with nitroimidazoles (including vaginal formulations), nitrofurans, and certain cephalosporins should be avoided if possible.', NULL, 'Until further information is available, use of ritonavir capsules, ritonavir oral solution, lopinavir-ritonavir oral solution, or tipranavir capsules with nitroimidazoles (including vaginal formulations), nitrofurans, and certain cephalosporins should be avoided if possible.', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/100731/', '[1] "Product Information. Norvir (ritonavir)." Abbott Pharmaceutical, Abbott Park, IL.[2] Williams CS, Woodcock KR "Do ethanol and metronidazole interact to produce a disulfiram-like reaction?." Ann Pharmacother 34 (2000): 255-7[3] Uri JV, Parks DB "Disulfiram-like reaction to certain cephalosporins." Ther Drug Monit 5 (1983): 219-24[4] Alexander I "Alcohol-antabuse syndrome in patients receiving metronidazole during gynaecological treatment." Br J Clin Pract 39 (1985): 292-3[5] "Product Information. Flagyl (metronidazole)." Searle, Skokie, IL.[6] "Product Information. Metrogel-Vaginal (metronidazole)." Curatek Pharmaceuticals Ltd, Elk Grove Village, IL.[7] "Product Information. Kaletra (lopinavir-ritonavir)" Abbott Pharmaceutical, Abbott Park, IL.[8] "Product Information. Tindamax (tinidazole)." Presutti Laboratories Inc, Arlington Heights, IL.[9] Neu HC, Prince AS "Interaction between moxalactam and alcohol." Lancet June (1980): 1422[10] Krulewitch CJ "An unexpected adverse drug effect." J Midwifery Womens Health 48 (2003): 67-8[11] Cina SJ, Russell RA, Conradi SE "Sudden death due to metronidazole/ethanol interaction." Am J Forensic Med Pathol 17 (1996): 343-6[12] Elenbaas RM "Drug therapy reviews: management of the disulfiram-alcohol reaction." Am J Hosp Pharm 34 (1977): 827-31[13] Freundt KJ, Schreiner E, Christmann-Kleiss U "Cefamandole: a competitive inhibitor of aldehyde dehydrogenase." Infection 13 (1985): 91[14] van Ieperen L "Sudden death during disulfiram-ethanol reaction." S Afr Med J 66 (1984): 165[15] Visapaa JP, Tillonen JS, Kaihovaara PS, Salaspuro MP "Lack of disulfiram-like reaction with metronidazole and ethanol." Ann Pharmacother 36 (2002): 971-4[16] Foster TS, Raehl CL, Wilson HD "Disulfiram-