INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (100655, 'Didanosine', 'Lopinavir', 'Moderate', 'INTERVAL: Since lopinavir-ritonavir oral solution must be taken with food, simultaneous administration with didanosine may interfere with the gastrointestinal absorption of didanosine, which should be taken on an empty stomach.', NULL, 'When prescribed in combination with lopinavir-ritonavir oral solution, didanosine should be administered on an empty stomach one hour before or two hours after lopinavir-ritonavir oral solution, which is to be taken with food.', NULL, 'When prescribed in combination with lopinavir-ritonavir oral solution, didanosine should be administered on an empty stomach one hour before or two hours after lopinavir-ritonavir oral solution, which is to be taken with food. Lopinavir-ritonavir tablets can be administered simultaneously with didanosine without food.', 'Absorption', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/100769/', '[1] Canadian Pharmacists Association "e-CPS. Available from: URL: http://www.pharmacists.ca/function/Subscriptions/ecps.cfm?link=eCPS_quikLink."[2] "Product Information. Kaletra (lopinavir-ritonavir)" Abbott Pharmaceutical, Abbott Park, IL.[3] Cato A, Qian J, Carothers L, et al. "Evaluation of the pharmacokinetic interaction between ritonavir and didanosine." Clin Pharmacol Ther 59 (1996): 144[4] "Product Information. Videx (didanosine)." Bristol-Myers Squibb, Princeton, NJ.[5] "Product Information. Kaletra (lopinavir-ritonavir)" Abbott Pharmaceutical, Abbott Park, IL.[6] "Product Information. Norvir (ritonavir)." Abbott Pharmaceutical, Abbott Park, IL.', 1767365206);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (100656, 'Digitoxin', 'Lopinavir', 'Moderate', 'Lopinavir in combination with ritonavir has been shown to prolong the PR interval of the electrocardiogram in some patients. Theoretically, coadministration with other agents that prolong the PR interval (e.g., beta blockers, calcium channel blockers, atazanavir, digoxin, lacosamide, mefloquine) may result in additive effects and increased risk of conduction disturbances and atrioventricular block.', NULL, 'Caution is advised if lopinavir-ritonavir is used concomitantly with other agents that prolong the PR interval, particularly those that are metabolized by CYP450 3A4 (e.', NULL, 'Caution is advised if lopinavir-ritonavir is used concomitantly with other agents that prolong the PR interval, particularly those that are metabolized by CYP450 3A4 (e.g., calcium channel blockers) because lopinavir-ritonavir is a potent inhibitor of the isoenzyme. The elderly as well as patients with underlying structural heart disease, preexisting conduction system abnormalities, ischemic heart disease, or cardiomyopathies may be at increased risk for developing cardiac conduction disturbances and atrioventricular block associated with the use of lopinavir-ritonavir. Patients should be advised to notify their physician if they experience dizziness, lightheadedness, fainting, or irregular heartbeat.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/100770/', '[1] "Product Information. Kaletra (lopinavir-ritonavir)" Abbott Pharmaceutical, Abbott Park, IL.[2] "Product Information. Kaletra (lopinavir-ritonavir)" Abbott Pharmaceutical, Abbott Park, IL.[3] "Product Information. Norvir (ritonavir)." Abbott Pharmaceutical, Abbott Park, IL.', 1767365206);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (100657, 'Dofetilide', 'Lopinavir', 'Major', 'Like other class III antiarrhythmic agents, dofetilide can cause dose-related QT interval prolongation. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', NULL, 'Coadministration of dofetilide with other drugs that can prolong the QT interval has not been studied and is not recommended.', NULL, 'Coadministration of dofetilide with other drugs that can prolong the QT interval has not been studied and is not recommended.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/100771/', '[1] Canadian Pharmacists Association "e-CPS. Available from: URL: http://www.pharmacists.ca/function/Subscriptions/ecps.cfm?link=eCPS_quikLink."[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[4] "Product Information. Tikosyn (dofetilide)" Pfizer US Pharmaceuticals, Atlanta, GA.[5] "Product Information. Tikosyn (dofetilide)" Pfizer US Pharmaceuticals, Atlanta, GA.[6] "Product Information. Kaletra (lopinavir-ritonavir)" Abbott Pharmaceutical, Abbott Park, IL.[7] "Product Information. Norvir (ritonavir)." Abbott Pharmaceutical, Abbott Park, IL.[8] "Product Information. Tikosyn (dofetilide)" Pfizer US Pharmaceuticals, Atlanta, GA.', 1767365206);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (100658, 'Dolasetron', 'Lopinavir', 'Major', 'Dolasetron can cause dose-related prolongation of the QT interval via its pharmacologically active metabolite, hydrodolasetron. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death. In general, the risk of an individual agent or a combination of agents causing ventricular arrhythmia in association with QT prolongation is largely unpredictable but may be increased by certain underlying risk factors such as congenital long QT syndrome, cardiac disease, and electrolyte disturbances (e.g., hypokalemia, hypomagnesemia). In addition, the extent of drug-induced QT prolongation is dependent on the particular drug(s) involved and dosage(s) of the drug(s). Dolasetron has been shown to cause dose-dependent prolongation of the PR and QRS intervals. There have been reports of second- and third-degree atrioventricular block, cardiac arrest, and serious ventricular arrhythmias including fatalities in both adult and pediatric patients. Theoretically, coadministration with other agents that prolong the PR interval (e.g., beta blockers; calcium channel blockers; some protease inhibitors such as atazanavir, lopinavir and saquinavir; digitalis; lacosamide; mefloquine; moricizine) or QRS interval (e.g., flecainide; moricizine; quinidine) may result in additive effects and increased risk of bradycardia and heart block. Other risk factors include underlying structural heart disease, preexisting conduction abnormalities, advanced age, sick sinus syndrome, atrial fibrillation with slow ventricular response, and myocardial ischemia.', NULL, 'Caution is advised if dolasetron is used concomitantly with other agents that prolong the QT, PR and/or QRS interval, especially in the elderly and patients with known risk factors.', NULL, 'Caution is advised if dolasetron is used concomitantly with other agents that prolong the QT, PR and/or QRS interval, especially in the elderly and patients with known risk factors. An ECG should be obtained in these patients before initiating dolasetron and periodically during treatment. Patients should be advised to seek medical attention if they experience dizziness, lightheadedness, fainting, palpitations, irregular heartbeat, shortness of breath, or syncope. Dolasetron should be avoided in patients with complete heart block or at risk for complete heart block, unless they have an implanted pacemaker.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/100772/', '[1] "Product Information. Anzemet (dolasetron)." Hoechst Marion-Roussel Inc, Kansas City, MO.[2] "Product Information. Kaletra (lopinavir-ritonavir)" Abbott Pharmaceutical, Abbott Park, IL.[3] "Product Information. Norvir (ritonavir)." Abbott Pharmaceutical, Abbott Park, IL.', 1767365206);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (100659, 'Donepezil', 'Lopinavir', 'Moderate', 'Since acetylcholinesterase inhibitors can cause bradycardia and heart block due to vagotonic effects on the sinoatrial and atrioventricular nodes, additive effects may occur with other agents that also possess bradycardic effects such as beta-blockers, calcium channel blockers, digitalis, some protease inhibitors (atazanavir, lopinavir-ritonavir, saquinavir), amiodarone, dronedarone, moricizine, lacosamide, and mefloquine.', NULL, 'Caution is advised if acetylcholinesterase inhibitors are used concomitantly with bradycardic drugs.', NULL, 'Caution is advised if acetylcholinesterase inhibitors are used concomitantly with bradycardic drugs. Patients with underlying structural heart disease, preexisting conduction system abnormalities, ischemic heart disease, or cardiomyopathies may be at increased risk for developing cardiac conduction disturbances and atrioventricular block. Patients should be advised to notify their physician if they experience dizziness, lightheadedness, fainting, or irregular heartbeat.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/100773/', '[1] "Product Information. Razadyne (galantamine)." Johnson and Johnson Medical Inc, Arlington, TX.[2] "Product Information. Cognex (tacrine)." Parke-Davis, Morris Plains, NJ.[3] Tiseo PJ, Perdomo CA, Friedhoff LT "Concurrent administration of donepezil HCI and digoxin: assessment of pharmacokinetic changes." Br J Clin Pharmacol 46(Suppl 1) (1998): 40-4[4] Tavassoli N, Sommet A, Lapeyre-Mestre M, Bagheri H, Montrastruc JL "Drug interactions with cholinesterase inhibitors : an analysis of the French pharmacovigilance database and a comparison of two national drug formularies (vidal, british national formulary)." Drug Saf 30 (2007): 1063-71[5] "Product Information. Exelon (rivastigmine)" Novartis Pharmaceuticals, East Hanover, NJ.[6] "Product Information. Aricept (donepezil)." Pfizer US Pharmaceuticals, New York, NY.', 1767365206);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (100660, 'Doxorubicin (liposomal)', 'Ritonavir', 'Moderate', 'Coadministration with ritonavir may increase the plasma concentrations of drugs that are substrates of the CYP450 3A4 isoenzyme. The mechanism is decreased clearance due to inhibition of CYP450 3A4 activity by ritonavir.', NULL, 'Caution is advised if ritonavir must be used concurrently with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range.', NULL, 'Caution is advised if ritonavir must be used concurrently with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever ritonavir is added to or withdrawn from therapy.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/100774/', '[1] "Product Information. Rapamune (sirolimus)" Wyeth-Ayerst Laboratories, Philadelphia, PA.[2] "Product Information. Norvir (ritonavir)." Abbott Pharmaceutical, Abbott Park, IL.[3] Olkkola KT, Palkama VJ, Neuvonen PJ "Ritonavir''s role in reducing fentanyl clearance and prolonging its half-life." Anesthesiology 91 (1999): 681-5[4] "Product Information. Kaletra (lopinavir-ritonavir)" Abbott Pharmaceutical, Abbott Park, IL.[5] "Product Information. Norvir (ritonavir)." Abbott Pharmaceutical, Abbott Park, IL.', 1767365206);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (100661, 'Droperidol', 'Lopinavir', 'Major', 'The use of droperidol has been associated with QT interval prolongation, torsade de pointes and other serious arrhythmias, and sudden death. The concurrent administration of agents that can produce hypokalemia and/or hypomagnesemia (e.g., potassium-wasting diuretics, amphotericin B, cation exchange resins), drugs known to increase the QT interval (e.g., phenothiazines, tricyclic antidepressants, antiarrhythmic agents, etc.), certain other drugs (benzodiazepines, volatile anesthetics, intravenous opiates), or alcohol abuse may increase the risk of prolonged QT syndrome.', NULL, 'The manufacturer recommends extreme caution if droperidol must be given concomitantly with these agents.', NULL, 'The manufacturer recommends extreme caution if droperidol must be given concomitantly with these agents. The dosage of droperidol should be individualized and titrated to the desired effect. Routine vital sign and ECG monitoring is recommended. When droperidol is used in combination with other drugs that cause CNS and/or respiratory depression, patients should be monitored for potentially excessive or prolonged CNS and respiratory depression.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/100775/', '[1] Canadian Pharmacists Association "e-CPS. Available from: URL: http://www.pharmacists.ca/function/Subscriptions/ecps.cfm?link=eCPS_quikLink."[2] EMA. European Medicines Agency. European Union "EMA - List of medicines under additional monitoring. Available from: URL: http://www.ema.europa.eu/ema/index.jsp?curl=pages/regulation/document_listing/document_listing_000366.jsp&mid=WC0b01ac058067c852" ([2013 - ]):[3] "Product Information. Inapsine (droperidol)." Janssen Pharmaceutica, Titusville, NJ.[4] Glassman AH, Bigger JT Jr "Antipsychotic drugs: prolonged QTc interval, torsade de pointes, and sudden death." Am J Psychiatry 158 (2001): 1774-82[5] Cerner Multum, Inc. "Australian Product Information." O 0[6] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[7] Cerner Multum, Inc. "Australian Product Information." O 0[8] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[9] Lemma GL, Desta Z, Jones DR, Gorski JC, Hall SD "Droperidol is predominantly metabolized by human CYP3A enzymes." Clin Pharmacol Ther 73 (2003): 16[10] "Product Information. Droperidol (droperidol)." Abbott Pharmaceutical, Abbott Park, IL.[11] Agencia Espaola de Medicamentos y Productos Sanitarios Healthcare "Centro de informacin online de medicamentos de la AEMPS - CIMA. Available from: URL: https://cima.aemps.es/cima/publico/home.html." ([2018]):[12] Cerner Multum, Inc "ANVISA Bulrio Eletrnico. Available from: URL: http://www.anvisa.gov.br/datavisa/fila_bula/index.asp." O 0 (2015):[13] "Product Information. Inapsine (droperidol)." Janssen Pharmaceutica, Titusville, NJ.[14] "Product Information. Kaletra (lopinavir-ritonavir)" Abbott Pharmaceutical, Abbott Park, IL.[15] "Product Information. Norvir (ritonavir)." Abbott Pharmaceutical, Abbott Park, IL.', 1767365206);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (100662, 'Echinacea', 'Ritonavir', 'Moderate', 'Coadministration with echinacea may alter the plasma concentrations and therapeutic effects of drugs that are substrates of CYP450 3A4. Echinacea appears to inhibit intestinal CYP450 3A4, which would lead to an increase in oral midazolam (a sensitive 3A4 substrate) bioavailability; however, plasma levels of midazolam following oral administration do not appear to be affected by echinacea. In contrast, it appears that echinacea may also induce hepatic CYP450 3A4; thereby increasing the hepatic clearance of drugs that are substrates of CYP450 3A4.', NULL, 'In general, patients should be advised to consult their healthcare provider before using any herbal or alternative medicines.', NULL, 'In general, patients should be advised to consult their healthcare provider before using any herbal or alternative medicines. If echinacea is prescribed with a drug that is a CYP450 3A4 substrate, the possibility of an altered (increased or decreased) therapeutic response should be considered. Patients should be monitored more closely following the addition or withdrawal of echinacea and the dosage of the CYP450 3A4 substrate adjusted as necessary.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/100776/', '[1] Gorski JC, Huang SM, Pinto A, et al. "The effect of echinacea (Echinacea purpurea root) on cytochrome P450 activity in vivo." Clin Pharmacol Ther 75 (2004): 89-100[2] Cerner Multum, Inc. "Australian Product Information." O 0', 1767365206);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (100663, 'Efavirenz', 'Lopinavir', 'Major', 'Coadministration with efavirenz may decrease the plasma concentrations of lopinavir, even in the presence of low-dose ritonavir as a pharmacokinetic booster. The proposed mechanism is efavirenz induction of lopinavir metabolism via CYP450 3A4.', NULL, 'A dosage increase is recommended for lopinavir-ritonavir when used in combination with efavirenz.', NULL, 'A dosage increase is recommended for lopinavir-ritonavir when used in combination with efavirenz. For adults, the dosage should be increased to 500 mg-125 mg (two 200 mg-50 mg tablets and one 100 mg-25 mg tablet) twice daily for the tablets and 533 mg-133 mg (6.5 mL) twice daily for the oral solution. Lopinavir-ritonavir should not be administered as a once daily regimen in combination with efavirenz. For pediatric patients 6 months to 18 years of age, the dosage should be increased to 300 mg-75 mg/m2 twice daily for the oral solution in both treatment-naive and treatment-experienced patients, not to exceed the recommended adult dosage of 533 mg-133 mg (6.5 mL) twice daily. If weight-based dosing is preferred, the recommended dosage is 13 mg-3.25 mg/kg given twice daily for patients under 15 kg and 11 mg-2.75 mg/kg given twice daily for patients 15 kg to 45 kg. Pediatric patients weighing more than 45 kg should receive the adult dosage of lopinavir-ritonavir.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/100777/', '[1] Canadian Pharmacists Association "e-CPS. Available from: URL: http://www.pharmacists.ca/function/Subscriptions/ecps.cfm?link=eCPS_quikLink."[2] "Product Information. Kaletra (lopinavir-ritonavir)" Abbott Pharmaceutical, Abbott Park, IL.[3] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[4] la Porte CJ, de Graaff-Teulen MJ, Colbers EP, et al. "Effect of efavirenz treatment on the pharmacokinetics of nelfinavir boosted by ritonavir in healthy volunteers." Br J Clin Pharmacol 58 (2004): 632-40[5] Aarnoutse RE, Grintjes KJ, Telgt DS, et al. "The influence of efavirenz on the pharmacokinetics of a twice-daily combination of indinavir and low-dose ritonavir in healthy volunteers." Clin Pharmacol Ther 71 (2002): 57-67[6] "Product Information. Sustiva (efavirenz)." DuPont Pharmaceuticals, Wilmington, DE.[7] "Product Information. Kaletra (lopinavir-ritonavir)" Abbott Pharmaceutical, Abbott Park, IL.[8] "Product Information. Sustiva (efavirenz)." DuPont Pharmaceuticals, Wilmington, DE.[9] "Product Information. Norvir (ritonavir)." Abbott Pharmaceutical, Abbott Park, IL.', 1767365206);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (100664, 'Eltrombopag', 'Lopinavir', 'Moderate', 'Coadministration with lopinavir-ritonavir has been shown to modestly decrease the plasma concentrations of eltrombopag.', NULL, 'No specific dosage adjustment is recommended when eltrombopag is used with lopinavir-ritonavir.', NULL, 'No specific dosage adjustment is recommended when eltrombopag is used with lopinavir-ritonavir. However, platelet count should be more closely monitored following initiation or discontinuation of lopinavir-ritonavir therapy, and the eltrombopag dosage adjusted as necessary.', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/100778/', '[1] "Product Information. Promacta (eltrombopag)." GlaxoSmithKline, Research Triangle Park, NC.[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] Canadian Pharmacists Association "e-CPS. Available from: URL: http://www.pharmacists.ca/function/Subscriptions/ecps.cfm?link=eCPS_quikLink."[4] Cerner Multum, Inc. "Australian Product Information." O 0[5] "Product Information. Kaletra (lopinavir-ritonavir)" Abbott Pharmaceutical, Abbott Park, IL.[6] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[7] "Product Information. Promacta (eltrombopag)." GlaxoSmithKline, Research Triangle Park, NC.[8] "Product Information. Norvir (ritonavir)." Abbott Pharmaceutical, Abbott Park, IL.', 1767365206);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (100665, 'Enfuvirtide', 'Ritonavir', 'Minor', 'Coadministration with ritonavir and/or saquinavir may slightly increase the plasma concentrations of enfuvirtide. The mechanism of interaction is unknown.', NULL, '-', NULL, '-', 'Synergism', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/100779/', '[1] "Product Information. Fuzeon (enfuvirtide)." Roche Laboratories, Nutley, NJ.[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] Bailey DG, Malcolm J, Arnold O, Spence JD "Grapefruit juice-drug interactions." Br J Clin Pharmacol 46 (1998): 101-10[4] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[5] Kupferschmidt HHT, Fattinger KE, Ha HR, Follath F, Krahenbuhl S "Grapefruit juice enhances the bioavailability of the HIV protease inhibitor saquinavir in man." Br J Clin Pharmacol 45 (1998): 355-9[6] Eagling VA, Profit L, Back DJ "Inhibition of the CYP3A4-mediated metabolism and P-glycoprotein-mediated transport of the HIV-I protease inhibitor saquinavir by grapefruit juice components." Br J Clin Pharmacol 48 (1999): 543-52[7] "Product Information. Invirase (saquinavir)." Roche Laboratories, Nutley, NJ.', 1767365206);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (100666, 'Entrectinib', 'Ritonavir', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of entrectinib, which is primarily metabolized by the isoenzyme. Increased exposure to entrectinib may increase the risk and/or severity of adverse effects such as cognitive impairment, mood disorders, dizziness, sleep disturbances, liver enzyme elevations, hyperuricemia, congestive heart failure, edema, myocarditis, QT prolongation, vision problems, anemia, and neutropenia.', NULL, 'Concomitant use of entrectinib with potent (or moderate) CYP450 3A4 inhibitors should be avoided when possible.', NULL, 'Concomitant use of entrectinib with potent (or moderate) CYP450 3A4 inhibitors should be avoided when possible. If coadministration is necessary, the dosage of entrectinib should be reduced to 100 mg once daily for adults and pediatrics patients (12 years and older) with a body surface area (BSA) greater than 1.5 m2. Following discontinuation, and after an appropriate washout period of the CYP450 3A4 inhibitor (i.e., 3 to 5 half-lives), the entrectinib dosage taken prior to initiating the CYP450 3A4 inhibitor may be resumed. For pediatric patients with a body surface area of 1.5 m2 or less, concomitant use of entrectinib with potent (or moderate) CYP450 3A4 inhibitors should be avoided.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/100780/', '[1] "Product Information. Rozlytrek (entrectinib)." Genentech, South San Francisco, CA.[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[4] "Product Information. Rozlytrek (entrectinib)." Genentech, South San Francisco, CA.', 1767365206);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (100667, 'Enzalutamide', 'Ritonavir', 'Major', 'Coadministration with drugs that are strong inducers of CYP450 3A4 may decrease the plasma concentrations of ritonavir and result in a potential loss of virologic response. The proposed mechanism is increased clearance due to induction of CYP450 3A4, which is the isoenzyme primarily responsible for the metabolism of ritonavir.', NULL, 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, concomitant use of ritonavir with drugs that are strong inducers of CYP450 3A4 is considered contraindicated.', NULL, 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, concomitant use of ritonavir with drugs that are strong inducers of CYP450 3A4 is considered contraindicated.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/100781/', '[1] "Product Information. Norvir (ritonavir)." Abbott Pharmaceutical, Abbott Park, IL.[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] Cerner Multum, Inc. "Australian Product Information." O 0[4] "Product Information. Orkambi (ivacaftor-lumacaftor)." Vertex Pharmaceuticals, Cambridge, MA.[5] "Product Information. Symdeko 4-Week (ivacaftor-tezacaftor)." Vertex Pharmaceuticals, Cambridge, MA.[6] "Product Information. Trikafta (elexacaftor/ivacaftor/tezacaftor)." Vertex Pharmaceuticals, Cambridge, MA.[7] "Product Information. Kalydeco (ivacaftor)." Vertex Pharmaceuticals, Cambridge, MA.[8] "Product Information. Trikafta (elexacaftor/ivacaftor/tezacaftor)." Vertex Pharmaceuticals, Cambridge, MA.[9] "Product Information. Kalydeco (ivacaftor)." Vertex Pharmaceuticals, Cambridge, MA.[10] "Product Information. Orkambi (ivacaftor-lumacaftor)." Vertex Pharmaceuticals, Cambridge, MA.[11] "Product Information. Symdeko 4-Week (ivacaftor-tezacaftor)." Vertex Pharmaceuticals, Cambridge, MA.[12] "Product Information. Norvir (ritonavir)." Abbott Pharmaceutical, Abbott Park, IL.', 1767365206);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (100668, 'Epirubicin', 'Ritonavir', 'Moderate', 'The concomitant or sequential use of epirubicin with agents known to induce hepatotoxicity may potentiate the risk of liver injury. Epirubicin undergoes elimination through the hepatobiliary system.', NULL, 'Caution and clinical and laboratory monitoring for signs of liver injury are advisable before, during, or after use of epirubicin with other agents that are potentially hepatotoxic.', NULL, 'Caution and clinical and laboratory monitoring for signs of liver injury are advisable before, during, or after use of epirubicin with other agents that are potentially hepatotoxic.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/100782/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] "Product Information. Ellence (epirubicin)" Pharmacia and Upjohn, Kalamazoo, MI.[4] "Product Information. Hivid (zalcitabine)." Roche Laboratories, Nutley, NJ.', 1767365206);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (100669, 'Eplerenone', 'Ritonavir', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations and risk of adverse reactions of eplerenone, which is primarily metabolized by the isoenzyme.', NULL, 'Concomitant use of eplerenone with potent CYP450 3A4 inhibitors is considered contraindicated.', NULL, 'Concomitant use of eplerenone with potent CYP450 3A4 inhibitors is considered contraindicated. Some authorities consider concomitant administration of eplerenone and itraconazole to be contraindicated during and for 2 weeks after treatment with itraconazole.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/100783/', '[1] "Product Information. Sporanox (itraconazole)." Janssen Pharmaceutica, Titusville, NJ.[2] "Product Information. Inspra (eplerenone)." Searle, Chicago, IL.[3] Cerner Multum, Inc. "Australian Product Information." O 0', 1767365206);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (100670, 'Eravacycline', 'Ritonavir', 'Minor', 'Coadministration with potent inhibitors of CYP450 3A4 may increase the plasma concentrations of eravacycline, which undergoes oxidation by CYP450 3A4 and flavin monoxygenase (FMO).', NULL, '-', NULL, '-', 'Metabolism', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/100784/', '[1] "Product Information. Xerava (eravacycline)." Tetraphase Pharmaceuticals, Inc, Watertown, MA.', 1767365206);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (100671, 'Erdafitinib', 'Ritonavir', 'Major', 'Coadministration of erdafitinib with potent inhibitors of CYP450 2C9 or 3A4 may increase the plasma concentrations and risk of toxicity of erdafitinib, which is a substrate of these isoenzymes.', NULL, 'Coadministration of erdafitinib with potent inhibitors of CYP450 2C9 or 3A4 should generally be avoided.', NULL, 'Coadministration of erdafitinib with potent inhibitors of CYP450 2C9 or 3A4 should generally be avoided. If concomitant use is unavoidable, patients should be closely monitored for the development of adverse effects. Dosage adjustments as well as clinical and laboratory monitoring of erdafitinib should be considered whenever a CYP450 2C9 or 3A4 inhibitor is added to or withdrawn from therapy.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/100785/', '[1] "Product Information. Balversa (erdafitinib)." Janssen Products, LP, Horsham, PA.', 1767365206);
