INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (100692, 'Febuxostat', 'Ritonavir', 'Minor', 'Potent inducers of UGT enzymes (e.g., rifampin, carbamazepine, phenobarbital, phenytoin, ritonavir) may increase the metabolism of febuxostat and decrease its therapeutic efficacy. Febuxostat undergoes extensive metabolism by both conjugation via UGT enzymes including UGT1A1, UGT1A3, UGT1A9, and UGT2B7, and oxidation via cytochrome P450 enzymes. The relative contribution of each isoenzyme and the clinical significance is unknown. Conversely, increased plasma levels of febuxostat may occur after a potent UGT inducer is discontinued.', NULL, '-', NULL, '-', 'Metabolism', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/100806/', '[1] EMEA. European Medicines Agency "EPARs. European Union Public Assessment Reports. Available from: URL: http://www.ema.europa.eu/ema/index.jsp?curl=pages/includes/medicines/medicines_landingpage.jsp&mid."[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0', 1767365206);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (100693, 'Fedratinib', 'Ritonavir', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of fedratinib, which is a substrate of the isoenzyme. The risk of adverse reactions such as anemia, thrombocytopenia, diarrhea, nausea, vomiting, and liver (ALT, AST) and pancreatic (amylase, lipase) enzyme elevations may increase.', NULL, 'Coadministration of fedratinib with potent CYP450 3A4 inhibitors should be avoided.', NULL, 'Coadministration of fedratinib with potent CYP450 3A4 inhibitors should be avoided. However, if coadministration with a potent CYP450 3A4 inhibitor is necessary, the manufacturer recommends that the dose of fedratinib be reduced to 200 mg once daily. Patients should be advised to notify their health care professional if signs and symptoms of anemia, thrombocytopenia, or gastrointestinal, hepatic, or pancreatic toxicity develop. Further dosage adjustment may be required based on patient tolerance and response. When/if concomitant therapy with a potent CYP450 3A4 inhibitor is ceased, the dose of fedratinib should first be increased to 300 mg once daily for two weeks an then increased to 400 mg once daily thereafter as tolerated.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/100807/', '[1] "Product Information. Inrebic (fedratinib)." Celgene Corporation, Summit, NJ.[2] Gunston GD, Mehta U "Potentially serious drug interactions with grapefruit juice." S Afr Med J 90 (2000): 41[3] Bailey DG, Arnold JMO, Spence JD "Grapefruit juice and drugs - how significant is the interaction." Clin Pharmacokinet 26 (1994): 91-8[