INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (100709, 'Flurbiprofen', 'Ritonavir', 'Minor', 'Ritonavir has a moderate affinity for the CYP450 2C9 microsomal enzyme and may variably affect drugs that are substrates of this enzyme. The area under the plasma concentration-time curves for these drugs may increase or decrease. If concomitant therapy is necessary, close monitoring for therapeutic and adverse effects is strongly recommended.', NULL, '-', NULL, '-', 'Metabolism', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/100823/', '[1] "Product Information. Norvir (ritonavir)." Abbott Pharmaceutical, Abbott Park, IL.[2] "Product Information. Kaletra (lopinavir-ritonavir)" Abbott Pharmaceutical, Abbott Park, IL.[3] "Product Information. Kaletra (lopinavir-ritonavir)" Abbott Pharmaceutical, Abbott Park, IL.[4] "Product Information. Accolate (zafirlukast)." Zeneca Pharmaceuticals, Wilmington, DE.[5] "Product Information. Norvir (ritonavir)." Abbott Pharmaceutical, Abbott Park, IL.', 1767365206);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (100710, 'Flutamide', 'Lopinavir', 'Moderate', 'Long-term androgen deprivation therapy can prolong the QT interval. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death. In addition, the extent of drug-induced QT prolongation is dependent on the particular drug(s) involved and dosage(s) of the drug(s).', NULL, 'The benefits of androgen deprivation therapy should be carefully assessed against the potential risk in patients receiving other drugs known to prolong the QT interval.', NULL, 'The benefits of androgen deprivation therapy should be carefully assessed against the potential risk in patients receiving other drugs known to prolong the QT interval. Electrolyte abnormalities should be corrected prior to initiating therapy, and periodic monitoring of electrocardiograms and electrolytes should be considered.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/100824/', '[1] "Product Information. Vantas (histrelin)." Endo Pharmaceuticals (formally Indevus Pharmaceuticals Inc), Lexington, MA.[2] "Product Information. Zoladex (goserelin)." Zeneca Pharmaceuticals, Wilmington, DE.[3] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[4] "Product Information. Trelstar (triptorelin)" Pharmacia and Upjohn, Kalamazoo, MI.[5] "Product Information. Firmagon (degarelix)." Ferring Pharmaceuticals Inc, Tarrytown, NY.[6] "Product Information. Lupron (leuprolide)." TAP Pharmaceuticals Inc, Deerfield, IL.[7] "Product Information. Eligard (leuprolide)." Sanofi Winthrop Pharmaceuticals, New York, NY.[8] "Product Information. Plenaxis (abarelix)." Praecis Pharmaceuticals Inc, Waltham, MA.', 1767365206);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (100711, 'Fluticasone', 'Ritonavir', 'Major', 'Coadministration with ritonavir may significantly increase the systemic exposure to fluticasone following intranasal administration or oral inhalation. The mechanism is ritonavir inhibition of fluticasone metabolism via hepatic and intestinal CYP450 3A4.', NULL, 'The use of intranasal or orally inhaled fluticasone in combination with ritonavir is not recommended unless the potential benefit outweighs the risk of systemic side effects.', NULL, 'The use of intranasal or orally inhaled fluticasone in combination with ritonavir is not recommended unless the potential benefit outweighs the risk of systemic side effects. Alternatives to fluticasone should be considered whenever possible if ritonavir must be used. A less potent, less lipophilic, and/or shorter-acting agent such as beclomethasone, budesonide, flunisolide or triamcinolone may be appropriate, although probably most, if not all, corticosteroids can interact with ritonavir to some extent. The lowest effective dosage of orally inhaled corticosteroid should be used, and further adjustments made as necessary according to therapeutic response and tolerance. Patients should be monitored for signs and symptoms of hypercorticism such as acne, striae, thinning of the skin, easy bruising, moon facies, dorsocervical "buffalo" hump, truncal obesity, increased appetite, acute weight gain, edema, hypertension, hirsutism, hyperhidrosis, proximal muscle wasting and weakness, glucose intolerance, exacerbation of preexisting diabetes, depression, and menstrual disorders. It is important to distinguish between hypercorticism and the lipodystrophy syndrome caused by antiretroviral treatment, as the overlap of certain clinical features may delay the recognition and diagnosis of Cushing''s syndrome. In general, the lack of peripheral atrophy and the presence of abdominal striae, easy bruising, and facial plethora would suggest iatrogenic Cushing''s syndrome rather than antiretroviral-related lipodystrophy. Other systemic glucocorticoid effects may include adrenal suppression, immunosuppression, posterior subcapsular cataracts, glaucoma, bone loss, and growth retardation in children and adolescents. Following extensive use with ritonavir, a progressive dosage reduction may be required over a longer period if fluticasone is to be withdrawn from therapy, as there may be a significant risk of adrenal suppression. Signs and symptoms of adrenal insufficiency include anorexia, hypoglycemia, nausea, vomiting, weight loss, muscle wasting, fatigue, weakness, dizziness, postural hypotension, depression, and adrenal crisis manifested as inability to respond to stress (e.g., illness, infection, surgery, trauma). Systemic glucocorticoids may be necessary until adrenal function recovers.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/100825/', '[1] Fardon TC, Lee DK, Haggart K, McFarlane LC, Lipworth BJ "Adrenal suppression with dry powder formulations of fluticasone propionate and mometasone furoate." Am J Respir Crit Care Med 170 (2004): 960-6[2] "Product Information. Flovent (fluticasone)." Glaxo Wellcome, Research Triangle Park, NC.[3] Rouanet I, Peyriere H, Mauboussin JM, Vincent D "Cushing''s syndrome in a patient treated by ritonavir/lopinavir and inhaled fluticasone." HIV Med 4 (2003): 149-50[4] "Product Information. Flonase (fluticasone)." Glaxo Wellcome, Research Triangle Park, NC.[5] Pessanha TM, Campos JM, Barros AC, Pone MV, Garrido JR, Pone SM "Iatrogenic Cushing''s syndrome in a adolescent with AIDSs on ritonavir and inhaled fluticasone. case report and literature rev