INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (100777, 'Isavuconazonium', 'Ritonavir', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of isavuconazole, which is primarily metabolized by CYP450 3A4 and 3A5 and subsequently by uridine diphosphate glucuronosyltransferases (UGT).', NULL, 'Concomitant use of isavuconazonium sulfate with potent CYP450 3A4 inhibitors is considered contraindicated.', NULL, 'Concomitant use of isavuconazonium sulfate with potent CYP450 3A4 inhibitors is considered contraindicated. Ritonavir given at low dosages as a pharmacokinetic booster may be used with caution, but is contraindicated at high dosages (e.g., 400 mg every 12 hours).', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/100891/', '[1] "Product Information. Cresemba (isavuconazonium)." Astellas Pharma US, Inc, Deerfield, IL.[2] Wohlt PD, Zheng L, Gunderson S, Balzar SA, Johnson BD, Fish JT "Recommendations for the use of medications with continuous enteral nutrition." Am J Health Syst Pharm 66 (2009): 1438-67[3] "Product Information. VFEND (voriconazole)." Pfizer U.S. Pharmaceuticals, New York, NY.', 1767365206);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (100778, 'Isoetharine', 'Lopinavir', 'Moderate', 'Beta-2 adrenergic agonists can cause dose-related prolongation of the QT interval and potassium loss. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death. Clinically significant prolongation of QT interval and hypokalemia occur infrequently when beta-2 agonists are inhaled at normally recommended dosages. However, these effects may be more common when the drugs are administered systemically or when recommended dosages are exceeded.', NULL, 'Caution is recommended if beta-2 agonists are used in combination with other drugs that can prolong the QT interval.', NULL, 'Caution is recommended if beta-2 agonists are used in combination with other drugs that can prolong the QT interval. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/100892/', '[1] "Product Information. Arcapta Neohaler (indacaterol)." Novartis Pharmaceuticals, East Hanover, NJ.[2] Whyte KF, Addis GJ, Whitesmith R, Reid JL "The mechanism of salbutamol-induced hypokalaemia." Br J Clin Pharmacol 23 (1987): 65-71[3] Sanders JP, Potter DE, Ellis S, Bee DE, Grant JA "Metabolic and cardiovascular effects of carbuterol and metaproterenol." J Allergy Clin Immunol 60 (1977): 174-9