INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (100781, 'Istradefylline', 'Ritonavir', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations and risk of adverse effects of istradefylline, which is primarily metabolized by CYP450 3A4 and 1A1.', NULL, 'The manufacturer suggests that the maximum dose of istradefylline when coadministered with potent CYP450 3A4 inhibitors be reduced to 20 mg once daily.', NULL, 'The manufacturer suggests that the maximum dose of istradefylline when coadministered with potent CYP450 3A4 inhibitors be reduced to 20 mg once daily. Patients should be monitored for adverse effects including dyskinesia, impulse control issues, dizziness, constipation, nausea, hallucinations, and insomnia.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/100895/', '[1] "Product Information. Nourianz (istradefylline)." Kyowa Kirin, Inc, Bedminster, NJ.[2] Wohlt PD, Zheng L, Gunderson S, Balzar SA, Johnson BD, Fish JT "Recommendations for the use of medications with continuous enteral nutrition." Am J Health Syst Pharm 66 (2009): 1438-67[3] "Product Information. VFEND (voriconazole)." Pfizer U.S. Pharmaceuticals, New York, NY.[4] "Product Information. Nourianz (istradefylline)." Kyowa Kirin, Inc, Bedminster, NJ.', 1767365206);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (100782, 'Itraconazole', 'Ritonavir', 'Major', 'Coadministration with ritonavir may significantly increase the plasma concentrations of itraconazole and ketoconazole. The mechanism is ritonavir inhibition of CYP450 3A4 metabolism.', NULL, 'During coadministration with ritonavir, the dosage of ketoconazole should generally not exceed 200 mg/day so as to avoid excessive plasma drug levels, which may be associated with impairment of testosterone and cortisol production and possibly an increased risk of hepatotoxicity.', NULL, 'During coadministration with ritonavir, the dosage of ketoconazole should generally not exceed 200 mg/day so as to avoid excessive plasma drug levels, which may be associated with impairment of testosterone and cortisol production and possibly an increased risk of hepatotoxicity. Some experts also recommend limiting the dosage of itraconazole to 200 mg/day when prescribed with ritonavir.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/100896/', '[1] "Product Information. Norvir (ritonavir)." Abbott Pharmaceutical, Abbott Park, IL.[2] Crommentuyn KM, Mulder JW, Sparidans RW, Huitema AD, Schellens JH, Beijnen JH "Drug-Drug Interaction between Itraconazole and the Antiretroviral Drug Lopinavir/Ritonavir in an HIV-1-Infected Patient with Disseminated Histoplasmosis." Clin Infect Dis 38 (2004): E73-5[3] Khaliq Y, Gallicano K, Venance S, Kravcik S, Cameron DW "Effect of ketoconazole on ritonavir and saquinavir concentrations in plasma and cerebrospinal fluid from patients infected with human immunodeficiency virus." Clin Pharmacol Ther 68 (2000): 637-46[4] Cerner Multum, Inc