INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (100783, 'Ivabradine', 'Ritonavir', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of ivabradine, which is primarily metabolized by the isoenzyme.', NULL, 'Concomitant use of ivabradine with potent CYP450 3A4 inhibitors is considered contraindicated.', NULL, 'Concomitant use of ivabradine with potent CYP450 3A4 inhibitors is considered contraindicated. Some authorities consider concomitant administration of ivabradine and itraconazole to be contraindicated during and for 2 weeks after treatment with itraconazole.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/100897/', '[1] Cerner Multum, Inc. "Australian Product Information." O 0[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] "Product Information. Sporanox (itraconazole)." Janssen Pharmaceutica, Titusville, NJ.[4] "Product Information. Corlanor (ivabradine)." Amgen USA, Thousand Oaks, CA.[5] "Product Information. Corlanor (ivabradine)." Amgen USA, Thousand Oaks, CA.[6] Cerner Multum, Inc. "Australian Product Information." O 0[7] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[8] Wohlt PD, Zheng L, Gunderson S, Balzar SA, Johnson BD, Fish JT "Recommendations for the use of medications with continuous enteral nutrition." Am J Health Syst Pharm 66 (2009): 1438-67[9] "Product Information. VFEND (voriconazole)." Pfizer U.S. Pharmaceuticals, New York, NY.', 1767365206);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (100784, 'Ivermectin', 'Ritonavir', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of ivermectin and the risk of adverse effects. The proposed mechanism, based on in vitro data, is decreased clearance due to inhibition of CYP450 3A4, the primary isoenzyme responsible for the metabolic clearance of ivermectin.', NULL, 'Caution is advised when ivermectin must be used concomitantly with potent CYP450 3A4 inhibitors.', NULL, 'Caution is advised when ivermectin must be used concomitantly with potent CYP450 3A4 inhibitors. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate whenever a potent CYP450 3A4 inhibitor is added to or withdrawn from therapy.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/100898/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] Zeng Z, Andrew NW, Arison BH, Luffer-Atlas D, Wang RW "Identification of cytochrome P4503A4 as the major enzyme responsible for the metabolism of ivermectin by human liver microsomes." Xenobiotica 28 (1998): 313-21[3] Wohlt PD, Zheng L, Gunderson S, Balzar SA, Johnson BD, Fish JT "Recommendations for the use of medications with continuous enteral nutrition." Am J Health Syst Pharm 66 (2009): 1438-67[4] "Product Information. VFEND (voriconazole)." Pfizer U.S. Pharmaceuticals, New York, NY.', 1767365206);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (100785, 'Ritonavir', 'Ivosidenib', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of ivosidenib, which is primarily metabolized by the isoenzyme. Coadministration with ivosidenib may decrease the plasma concentrations of drugs that are primarily metabolized by CYP450 3A4, including many of the known potent inhibitors of the isoenzyme such as clarithromycin, conivaptan, delavirdine, idelalisib, itraconazole, ketoconazole, nefazodone, posaconazole, telithromycin, and all protease inhibitors. Ivosidenib is an inducer of CYP450 3A4 and induces its own metabolism following multiple dosing.', NULL, 'Concomitant use of ivosidenib with potent CYP450 3A4 inhibitors, particularly those that are also substrates of CYP450 3A4, should generally be avoided.', NULL, 'Concomitant use of ivosidenib with potent CYP450 3A4 inhibitors, particularly those that are also substrates of CYP450 3A4, should generally be avoided. If coadministration is required, a reduction of the ivosidenib dosage to 250 mg once daily is recommended. Following discontinuation of the potent inhibitor for at least 5 half-lives, ivosidenib therapy should be returned to the recommended dosage of 500 mg once daily. Monitoring of electrocardiograms and electrolytes should be conducted regularly.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/100899/', '[1] "Product Information. Tibsovo (ivosidenib)." Agios Pharmaceuticals, Cambridge, MA.[2] "Product Information. Tibsovo (ivosidenib)." Agios Pharmaceuticals, Cambridge, MA.[3] Wohlt PD, Zheng L, Gunderson S, Balzar SA, Johnson BD, Fish JT "Recommendations for the use of medications with continuous enteral nutrition." Am J Health Syst Pharm 66 (2009): 1438-67[4] "Product Information. VFEND (voriconazole)." Pfizer U.S. Pharmaceuticals, New York, NY.', 1767365206);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (100786, 'Ritonavir', 'Ixabepilone', 'Moderate', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of ixabepilone, which is primarily metabolized by the isoenzyme.', NULL, 'Concomitant use of ixabepilone with potent CYP450 3A4 inhibitors should generally be avoided.', NULL, 'Concomitant use of ixabepilone with potent CYP450 3A4 inhibitors should generally be avoided. If coadministration is necessary, a reduction of the ixabepilone dosage to 20 mg/m2 should be considered. Based on pharmacokinetic studies, this dosage is predicted to adjust the ixabepilone systemic exposure (AUC) to the range observed without inhibitors. However, clinical data are lacking. Following discontinuation of the potent CYP450 3A4 inhibitor, a washout period of approximately one week should be allowed before the ixabepilone dosage is adjusted upward to the indicated dosage.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/100900/', '[1] "Product Information. Ixempra (ixabepilone)." Bristol-Myers Squibb, Princeton, NJ.[2] Wohlt PD, Zheng L, Gunderson S, Balzar SA, Johnson BD, Fish JT "Recommendations for the use of medications with continuous enteral nutrition." Am J Health Syst Pharm 66 (2009): 1438-67[3] "Product Information. VFEND (voriconazole)." Pfizer U.S. Pharmaceuticals, New York, NY.[4] "Product Information. Ixempra (ixabepilone)." Bristol-Myers Squibb, Princeton, NJ.', 1767365206);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (100787, 'Ritonavir', 'Ketoconazole', 'Major', 'Coadministration with ritonavir may significantly increase the plasma concentrations of itraconazole and ketoconazole. The mechanism is ritonavir inhibition of CYP450 3A4 metabolism.', NULL, 'During coadministration with ritonavir, the dosage of ketoconazole should generally not exceed 200 mg/day so as to avoid excessive plasma drug levels, which may be associated with impairment of testosterone and cortisol production and possibly an increased risk of hepatotoxicity.', NULL, 'During coadministration with ritonavir, the dosage of ketoconazole should generally not exceed 200 mg/day so as to avoid excessive plasma drug levels, which may be associated with impairment of testosterone and cortisol production and possibly an increased risk of hepatotoxicity. Some experts also recommend limiting the dosage of itraconazole to 200 mg/day when prescribed with ritonavir.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/100901/', '[1] "Product Information. Norvir (ritonavir)." Abbott Pharmaceutical, Abbott Park, IL.[2] Crommentuyn KM, Mulder JW, Sparidans RW, Huitema AD, Schellens JH, Beijnen JH "Drug-Drug Interaction between Itraconazole and the Antiretroviral Drug Lopinavir/Ritonavir in an HIV-1-Infected Patient with Disseminated Histoplasmosis." Clin Infect Dis 38 (2004): E73-5[3] Khaliq Y, Gallicano K, Venance S, Kravcik S, Cameron DW "Effect of ketoconazole on ritonavir and saquinavir concentrations in plasma and cerebrospinal fluid from patients infected with human immunodeficiency virus." Clin Pharmacol Ther 68 (2000): 637-46[4] Cerner Multum, Inc. "Australian Product Information." O 0[5] "Product Information. Kaletra (lopinavir-ritonavir)" Abbott Pharmaceutical, Abbott Park, IL.[6] Canadian Pharmacists Association "e-CPS. Available from: URL: http://www.pharmacists.ca/function/Subscriptions/ecps.cfm?link=eCPS_quikLink."[7] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[8] Anson BD, Weaver JG, Ackerman MJ, et al. "Blockade of HERG channels by HIV protease inhibitors." Lancet 365 (2005): 682-686[9] "Product Information. Kaletra (lopinavir-ritonavir)" Abbott Pharmaceutical, Abbott Park, IL.[10] "Product Information. Norvir (ritonavir)." Abbott Pharmaceutical, Abbott Park, IL.', 1767365206);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (100788, 'Ritonavir', 'Labetalol', 'Moderate', 'Coadministration with ritonavir may increase the plasma concentrations of drugs that are substrates of the CYP450 2D6 isoenzyme. The mechanism is decreased clearance due to competitive inhibition of CYP450 2D6 activity by ritonavir.', NULL, 'Caution is advised if ritonavir must be used concurrently with medications that undergo metabolism by CYP450 2D6, particularly those with a narrow therapeutic range.', NULL, 'Caution is advised if ritonavir must be used concurrently with medications that undergo metabolism by CYP450 2D6, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever ritonavir is added to or withdrawn from therapy.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/100902/', '[1] "Product Information. Norvir (ritonavir)." Abbott Pharmaceutical, Abbott Park, IL.[2] "Product Information. Norvir (ritonavir)." Abbott Pharmaceutical, Abbott Park, IL.[3] "Product Information. Viekira Pak (dasabuvir/ombitasvir/paritaprevir/ritonavir)." AbbVie US LLC, North Chicago, IL.', 1767365206);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (100789, 'Lopinavir', 'Lacosamide', 'Moderate', 'Lopinavir in combination with ritonavir has been shown to prolong the PR interval of the electrocardiogram in some patients. Theoretically, coadministration with other agents that prolong the PR interval (e.g., beta blockers, calcium channel blockers, atazanavir, digoxin, lacosamide, mefloquine) may result in additive effects and increased risk of conduction disturbances and atrioventricular block.', NULL, 'Caution is advised if lopinavir-ritonavir is used concomitantly with other agents that prolong the PR interval, particularly those that are metabolized by CYP450 3A4 (e.', NULL, 'Caution is advised if lopinavir-ritonavir is used concomitantly with other agents that prolong the PR interval, particularly those that are metabolized by CYP450 3A4 (e.g., calcium channel blockers) because lopinavir-ritonavir is a potent inhibitor of the isoenzyme. The elderly as well as patients with underlying structural heart disease, preexisting conduction system abnormalities, ischemic heart disease, or cardiomyopathies may be at increased risk for developing cardiac conduction disturbances and atrioventricular block associated with the use of lopinavir-ritonavir. Patients should be advised to notify their physician if they experience dizziness, lightheadedness, fainting, or irregular heartbeat.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/100903/', '[1] "Product Information. Kaletra (lopinavir-ritonavir)" Abbott Pharmaceutical, Abbott Park, IL.[2] "Product Information. Kaletra (lopinavir-ritonavir)" Abbott Pharmaceutical, Abbott Park, IL.[3] "Product Information. Norvir (ritonavir)." Abbott Pharmaceutical, Abbott Park, IL.', 1767365206);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (100790, 'Lopinavir', 'Lactitol', 'Moderate', 'Bowel cleansing as well as overuse of certain laxatives may cause electrolyte loss and increase the risk of torsade de pointes ventricular arrhythmia in patients treated with drugs that prolong the QT interval. Electrolyte disturbances including hypokalemia and hypomagnesemia have been reported with laxative abuse and are known risk factors for torsade de pointes associated with QT interval prolongation.', NULL, 'Patients treated with drugs that prolong the QT interval should exercise caution when self-medicating with laxatives.', NULL, 'Patients treated with drugs that prolong the QT interval should exercise caution when self-medicating with laxatives. The recommended dosage and duration of use should not be exceeded. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/100904/', '[1] Cerner Multum, Inc. "Australian Product Information." O 0[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] Schaefer DC, Cheskin LJ "Constipation in the elderly." Am Fam Physician 58 (1998): 907-14[4] Muller-Lissner SA "Adverse effects of laxatives: fact and fiction." Pharmacology 47 (1993): 138-45[5] Canadian Pharmacists Association "e-CPS. Available from: URL: http://www.pharmacists.ca/function/Subscriptions/ecps.cfm?link=eCPS_quikLink."[6] Chin RL "Laxative-induced hypokalemia." Ann Emerg Med 32 (1998): 517-8', 1767365206);
