INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (100854, 'Ritonavir', 'Miconazole', 'Minor', 'The coadministration with fluconazole may slightly increase the plasma concentrations of ritonavir. The proposed mechanism is fluconazole inhibition of CYP450 3A4, the isoenzyme responsible for the metabolic clearance of ritonavir. Other azole antifungal agents are expected to affect ritonavir in a similar fashion but possibly to a greater extent, since fluconazole is a relatively weak inhibitor of CYP450 3A4. No special precautions are necessary when fluconazole is given with ritonavir.', NULL, '-', NULL, '-', 'Metabolism', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/100968/', '[1] Koks CH, Crommentuyn KM, Hoetelmans RM, et al. "The effect of fluconazole on ritonavir and saquinavir pharmacokinetics in HIV-1-infected individuals." Br J Clin Pharmacol 51 (2001): 631-5[2] Cato A 3rd, Cao G, Hsu A, Cavanaugh J, Leonard J, Granneman R "Evaluation of the effect of fluconazole on the pharmacokinetics of ritonavir." Drug Metab Dispos 25 (1997): 1104-6[3] "Product Information. Norvir (ritonavir)." Abbott Pharmaceutical, Abbott Park, IL.[4] "Product Information. Viekira Pak (dasabuvir/ombitasvir/paritaprevir/ritonavir)." AbbVie US LLC, North Chicago, IL.', 1767365206);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (100855, 'Ritonavir', 'Midazolam', 'Major', 'Coadministration with protease inhibitors (PIs) may significantly increase the plasma concentrations and pharmacologic effects of orally administered midazolam and triazolam. The mechanism is PI inhibition of intestinal and hepatic CYP450 3A4, the isoenzyme primarily responsible for the metabolic clearance of these benzodiazepines. Within the class, ritonavir is the most potent CYP450 3A4 inhibitor, while saquinavir is considered one of the weaker ones.', NULL, 'Given the potential for prolonged and/or increased sedation and respiratory depression associated with excessive benzodiazepine blood levels, concomitant use of oral midazolam or triazolam with protease inhibitors is considered contraindicated.', NULL, 'Given the potential for prolonged and/or increased sedation and respiratory depression associated with excessive benzodiazepine blood levels, concomitant use of oral midazolam or triazolam with protease inhibitors is considered contraindicated. Caution and close clinical monitoring are recommended when administering parenteral midazolam in combination with protease inhibitors. Appropriate medical management should be readily available in case of respiratory depression and/or prolonged sedation. Dosage adjustment for midazolam may be appropriate, especially if more than a single dose of midazolam is administered.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/100969/', '[1] Eagling VA, Back DJ, Barry MG "Differential inhibition of cytochrome P450 isoforms by the protease inhibitors, ritonavir, saquinavir and indinavir." Br J Clin Pharmac