INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (100872, 'Ritonavir', 'Naltrexone', 'Moderate', 'Coadministration of naltrexone with other agents known to induce hepatotoxicity may potentiate the risk of liver injury. Naltrexone, especially in larger than recommended doses (more than 50 mg/day), has been associated with hepatocellular injury, hepatitis, and elevations in liver transaminases and bilirubin. Other potential causative or contributory etiologies identified include preexisting alcoholic liver disease, hepatitis B and/or C infection, and concomitant usage of other hepatotoxic drugs.', NULL, 'The use of naltrexone with other potentially hepatotoxic agents should be avoided whenever possible (e.', NULL, 'The use of naltrexone with other potentially hepatotoxic agents should be avoided whenever possible (e.g., acetaminophen; alcohol; androgens and anabolic steroids; antituberculous agents; azole antifungal agents; ACE inhibitors; cyclosporine (high dosages); disulfiram; endothelin receptor antagonists; interferons; ketolide and macrolide antibiotics; kinase inhibitors; minocycline; nonsteroidal anti-inflammatory agents; nucleoside reverse transcriptase inhibitors; proteasome inhibitors; retinoids; sulfonamides; tamoxifen; thiazolidinediones; tolvaptan; vincristine; zileuton; anticonvulsants such as carbamazepine, hydantoins, felbamate, and valproic acid; lipid-lowering medications such as fenofibrate, lomitapide, mipomersen, niacin, and statins; herbals and nutritional supplements such as black cohosh, chaparral, comfrey, DHEA, kava, pennyroyal oil, and red yeast rice). Patients should be advised to seek medical attention if they experience potential signs and symptoms of hepatotoxicity such as fever, rash, itching, anorexia, nausea, vomiting, fatigue, malaise, right upper quadrant pain, dark urine, pale stools, and jaundice. Periodic monitoring of hepatic function is advisable.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/100986/', '[1] "Product Information. ReVia (naltrexone)." DuPont Pharmaceuticals, Wilmington, DE.', 1767365206);
