INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (100877, 'Nelfinavir', 'Lopinavir', 'Major', 'Coadministration with nelfinavir may decrease the plasma concentrations of lopinavir, even in the presence of low-dose ritonavir as a pharmacokinetic booster. The proposed mechanism is nelfinavir induction of lopinavir metabolism via CYP450 3A4.', NULL, 'A dosage increase is recommended for lopinavir-ritonavir when used in combination with nelfinavir.', NULL, 'A dosage increase is recommended for lopinavir-ritonavir when used in combination with nelfinavir. For adults, the dosage should be increased to 500 mg-125 mg (two 200 mg-50 mg tablets and one 100 mg-25 mg tablet) twice daily for the tablets and 533 mg-133 mg (6.5 mL) twice daily for the oral solution. Lopinavir-ritonavir should not be administered as a once daily regimen in combination with nelfinavir. For pediatric patients 6 months to 18 years of age, the dosage should be increased to 300 mg-75 mg/m2 twice daily for the oral solution in both treatment-naive and treatment-experienced patients, not to exceed the recommended adult dosage of 533 mg-133 mg (6.5 mL) twice daily. If weight-based dosing is preferred, the recommended dosage is 13 mg-3.25 mg/kg given twice daily for patients under 15 kg and 11 mg-2.75 mg/kg given twice daily for patients 15 kg to 45 kg. Pediatric patients weighing more than 45 kg should receive the adult dosage of lopinavir-ritonavir. Please consult the manufacturer''s product labeling for pediatric dosing of the tablet formulation. Lopinavir-ritonavir should not be administered in combination with nelfinavir in patients under 6 months of age due to the lack of clinical data.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/100991/', '[1] "Product Information. Kaletra (lopinavir-ritonavir)" Abbott Pharmaceutical, Abbott Park, IL.[2] Canadian Pharmacists Association "e-CPS. Available from: URL: http://www.pharmacists.ca/function/Subscriptions/ecps.cfm?link=eCPS_quikLink."[3] Kempf DJ, Marsh KC, Kumar G, et al "Pharmacokinetic enhancement of inhibitors of the human immunodeficiency virus protease by coadministration with ritonavir." Antimicrob Agents Chemother 41 (1997): 654-60[4] Lu JF, Blaschke TF, Flexner C, Rosenkranz SL, Sheiner LB "Model-based Analysis of the Pharmacokinetic Interactions Between Ritonavir, Nelfinavir, and Saquinavir after Simultaneous and Staggered Oral Administration." Drug Metab Dispos 30 (2002): 1455-61[5] Kempf D, Marsh K, Denissen J, Kumar G, Rodrigues D, McDonald E, Flentge C, Green B, Chen X, Leonard J, Norbeck D "Coadministration with ritonavir enhances the plasma levels of HIV protease inhibitors by inhibition of cytochrome P450." 3rd Conf Retro and Opportun Infect (1996): 79[6] "Product Information. Viracept (nelfinavir)." Agouron Pharma Inc, La Jolla, CA.[7] "Product Information. Kaletra (lopinavir-ritonavir)" Abbott Pharmaceutical, Abbott Park, IL.[8] "Product Information. Norvir (ritonavir)." Abbott Pharmaceutical, Abbott Park, IL.', 1767365206);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (100878, 'Ritonavir', 'Neratinib', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 such as ritonavir may significantly increase the plasma concentrations and risk of toxicity of of neratinib, which is primarily metabolized by the isoenzyme.', NULL, 'Given the potential for increased risk of toxicity, concomitant use of neratinib with potent inhibitors of CYP450 3A4 such as ritonavir should generally be avoided.', NULL, 'Given the potential for increased risk of toxicity, concomitant use of neratinib with potent inhibitors of CYP450 3A4 such as ritonavir should generally be avoided. According to some authorities, if the CYP450 3A4 inhibitor cannot be avoided, the dose of neratinib should be reduced to 40 mg once daily with a strong CYP450 3A4 inhibitor and 200 mg once daily with a moderate CYP450 3A4 inhibitor. The previous dose of neratinib may be resumed following discontinuation of a strong or moderate CYP450 3A4 inhibitor.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/100992/', '[1] "Product Information. Nerlynx (neratinib)." Puma Biotechnology, Inc., Los Angeles, CA.[2] "Product Information. Norvir (ritonavir)." Abbott Pharmaceutical, Abbott Park, IL.[3] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[4] Cerner Multum, Inc. "Australian Product Information." O 0[5] Cerner Multum, Inc. "Australian Product Information." O 0[6] Abbas R, Hug BA, Leister C, Burns J, Sonnichsen D "Pharmacokinetics of oral neratinib during co-administration of ketoconazole in healthy subjects." Br J Clin Pharmacol 71 (2011): 522-7[7] "Product Information. Nerlynx (neratinib)