INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (100898, 'Ritonavir', 'Orlistat', 'Moderate', 'Orlistat may theoretically reduce the gastrointestinal absorption and therapeutic efficacy of antiretroviral agents. The mechanism may involve retention of lipophilic medicines in the gastrointestinal tract or reduced gastrointestinal tract transit time.', NULL, 'The potential reduction in antiretroviral efficacy should be considered before initiating treatment with orlistat.', NULL, 'The potential reduction in antiretroviral efficacy should be considered before initiating treatment with orlistat. Patients who take antiretroviral medications should be advised to consult their physician before using the non-prescription brand of orlistat.', 'Absorption', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/101012/', '[1] MHRA. Medicines and Healthcare Products Regulatory Agency "Orlistat: theoretical interaction with antiretroviral HIV medicines. Available from: URL: http://www.mhra.gov.uk/Safetyinformation/DrugSafetyUpdate/CON392868" ([2014 Mar]):[2] "Product Information. Hivid (zalcitabine)." Roche Laboratories, Nutley, NJ.', 1767365206);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (100899, 'Ritonavir', 'Osilodrostat', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may increase the plasma concentrations of osilodrostat, which is partially metabolized by the isoenzyme. Clinically, high plasma levels of osilodrostat may increase the risk of adverse effects such as hypocortisolism (which may lead to life-threatening adrenal insufficiency), QT prolongation (which may increase the risk of ventricular arrhythmias including torsade de pointes and sudden death), and elevated androgen and 11-deoxycorticosterone levels (the latter of which may activate mineralocorticoid receptors and cause hypokalemia, edema, and hypertension).', NULL, 'The manufacturer recommends reducing the dosage of osilodrostat by one-half during concomitant use of a potent CYP450 3A4 inhibitor.', NULL, 'The manufacturer recommends reducing the dosage of osilodrostat by one-half during concomitant use of a potent CYP450 3A4 inhibitor. Further dosage adjustments should be based on clinical response and tolerance. Patients should have regular monitoring of 24-hour urine free cortisol and serum or plasma cortisol during treatment, as well as regular evaluations for signs and symptoms of hypocortisolism such as nausea, vomiting, abdominal pain, loss of appetite, fatigue, dizziness, hypotension, abnormal electrolyte levels, and hypoglycemia. Decrease dosing or temporarily discontinue osilodrostat if patients experience symptoms of hypocortisolism or if urine free cortisol levels fall below the target range or there is a rapid decrease in cortisol levels. Stop osilodrostat and administer exogenous glucocorticoid replacement therapy if patients have symptoms of adrenal insufficiency and serum or plasma cortisol levels are below target range. Osilodrostat therapy may be restarted at a lower dosage when symptoms have resolved and cortisol values are within target range. Additionally, an electrocardiogram and serum electrolyte levels should also be obtained prior to initiating osilodrostat therapy and periodically thereafter.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/101013/', '[1] "Product Information. Isturisa (osilodrostat)." Recordati Rare Diseases Inc, Lebanon, NJ.[2] Wohlt PD, Zheng L, Gunderson S, Balzar SA, Johnson BD, Fish JT "Recommendations for the use of medications with continuous enteral nutrition." Am J Health Syst Pharm 66 (2009): 1438-67[3] "Product Information. VFEND (voriconazole)." Pfizer U.S. Pharmaceuticals, New York, NY.', 1767365206);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (100900, 'Ritonavir', 'Osimertinib', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may increase the plasma concentrations of osimertinib, which has been shown in vitro to be primarily metabolized by the isoenzyme. However, no pharmacokinetic data are available from drug interaction studies. Because osimertinib is associated with concentration-dependent prolongation of the QT interval, increased levels may potentiate the risk of ventricular arrhythmias such as torsade de pointes and sudden death.', NULL, 'Concomitant use of osimertinib with potent CYP450 3A4 inhibitors should generally be avoided.', NULL, 'Concomitant use of osimertinib with potent CYP450 3A4 inhibitors should generally be avoided. If coadministration is required, close monitoring for adverse effects such as diarrhea, QT prolongation, torsade de pointes arrhythmia, and cardiomyopathy (e.g., cardiac failure, pulmonary edema, ejection fraction decreases) is recommended, and the osimertinib dosage adjusted accordingly or treatment discontinued as necessary.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/101014/', '[1] "Product Information. Tagrisso (osimertinib)." Astra-Zeneca Pharmaceuticals, Wilmington, DE.[2] Wohlt PD, Zheng L, Gunderson S, Balzar SA, Johnson BD, Fish JT "Recommendations for the use of medications with continuous enteral nutrition." Am J Health Syst Pharm 66 (2009): 1438-67[3] "Product Information. VFEND (voriconazole)." Pfizer U.S. Pharmaceuticals, New York, NY.', 1767365206);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (100901, 'Ritonavir', 'Ospemifene', 'Moderate', 'Coadministration with potent inhibitors of CYP450 3A4 may increase the plasma concentrations of ospemifene, which is partially metabolized by the isoenzyme.', NULL, 'Caution is advised when ospemifene is used with potent CYP450 3A4 inhibitors.', NULL, 'Caution is advised when ospemifene is used with potent CYP450 3A4 inhibitors. Patients should be advised to seek medical attention if they develop potential signs and symptoms of thromboembolism such as chest pain, shortness of breath, and pain or swelling in the arms or legs.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/101015/', '[1] "Product Information. Osphena (ospemifene)." Shionogi USA Inc, Florham Park, NJ.[2] "Product Information. Zokinvy (lonafarnib)." Eiger BioPharmaceuticals, Palo Alto, CA.[3] "Product Information. Osphena (ospemifene)." Shionogi USA Inc, Florham Park, NJ.', 1767365206);
