INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (100908, 'Ritonavir', 'Paclitaxel (protein-bound)', 'Major', 'Coadministration with certain antiretroviral agents such as protease inhibitors and delavirdine may increase the plasma concentrations and pharmacologic effects of paclitaxel. The proposed mechanism is inhibition of the CYP450 3A4-mediated metabolism of paclitaxel.', NULL, 'Caution is advised if paclitaxel is required in patients receiving protease inhibitors and/or delavirdine.', NULL, 'Caution is advised if paclitaxel is required in patients receiving protease inhibitors and/or delavirdine. A lower initial dosage of paclitaxel may be appropriate. Patients should be closely monitored for the development of dose-related paclitaxel toxicity such as myelosuppression, stomatitis, arthralgia, myalgia, visual disturbances and peripheral neuropathy, and the paclitaxel dosage further adjusted as necessary. Use of repaglinide with the fixed combination of atazanavir-cobicistat is not recommended.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/101022/', '[1] Bundow D, Aboulafia DM "Potential drug interaction with paclitaxel and highly active antiretroviral therapy in two patients with AIDS-associated Kaposi sarcoma." Am J Clin Oncol 27 (2004): 81-4[2] "Product Information. Taxol (paclitaxel)." Bristol-Myers Squibb, Princeton, NJ.[3] "Product Information. Taxotere (docetaxel)." Rhone-Poulenc Rorer, Collegeville, PA.[4] Panday VRN, Hoetelmans RMW, vanHeeswijk RPG, Meenhorst PL, Inghels M, Mulder JW, Beijnen JH "Paclitaxel in the treatment of human immunodeficiency virus 1-associated Kaposi''s sarcoma - drug-drug interactions with proteas