INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (100931, 'Ritonavir', 'Pimavanserin', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of pimavanserin, which is primarily metabolized by the isoenzyme. High plasma levels of pimavanserin may increase the risk of QT interval prolongation, which has been associated with ventricular arrhythmias including torsade de pointes and sudden death.', NULL, 'Pimavanserin dosage should be reduced to 10 mg once a day when used with potent CYP450 3A4 inhibitors.', NULL, 'Pimavanserin dosage should be reduced to 10 mg once a day when used with potent CYP450 3A4 inhibitors.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/101045/', '[1] "Product Information. Nuplazid (pimavanserin)." Accelis Pharma, East Windsor, NJ.[2] "Product Information. Zokinvy (lonafarnib)." Eiger BioPharmaceuticals, Palo Alto, CA.', 1767365206);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (100932, 'Ritonavir', 'Pimecrolimus (topical)', 'Minor', 'Theoretically, coadministration with CYP450 3A inhibitors may increase the plasma concentrations of pimecrolimus, which has been shown in vitro to undergo metabolism by this subfamily of hepatic microsomal enzymes. Although clinically significant drug interactions are not expected due to minimal systemic absorption of topically administered pimecrolimus, the possibility cannot be ruled out.', NULL, '-', NULL, '-', 'Metabolism', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/101046/', '[1] "Product Information. Elidel (pimecrolimus topical)." Novartis Pharmaceuticals, East Hanover, NJ.', 1767365206);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (100933, 'Ritonavir', 'Pimozide', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of pimozide, which is partially metabolized by the isoenzyme. The use of pimozide has been associated with dose-related prolongation of the QT interval, thus elevated plasma levels of the drug may potentiate the risk of ventricular arrhythmias such as ventricular tachycardia and torsade de pointes as well as cardiac arrest and sudden death.', NULL, 'Given the potential for serious and life-threatening adverse cardiac events associated with increased plasma levels of pimozide, concomitant use with potent CYP450 3A4 inhibitors such as itraconazole, ketoconazole, posaconazole, voriconazole, clarithromycin, telithromycin, conivaptan, idelalisib, nefazodone, cobicistat, delavirdine, and most protease inhibitors is considered contraindicated.', NULL, 'Given the potential for serious and life-threatening adverse cardiac events associated with increased plasma levels of pimozide, concomitant use with potent CYP450 3A4 inhibitors such as itraconazole, ketoconazole, posaconazole, voriconazole, clarithromycin, telithromycin, conivaptan, idelalisib, nefazodone, cobicistat, delavirdine, and most protease inhibitors is considered contraindicated. Some authorities consider concomitant administration of lurasidone and itraconazole to be contraindicated during and for 2 weeks after treatment with itraconazole. With respect to less potent CYP450 3A4 inhibitors, the manufacturers recommend that they also not be used with pimozide.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.