INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (100934, 'Ritonavir', 'Pindolol', 'Moderate', 'Coadministration with ritonavir may increase the plasma concentrations of drugs that are substrates of the CYP450 2D6 isoenzyme. The mechanism is decreased clearance due to competitive inhibition of CYP450 2D6 activity by ritonavir.', NULL, 'Caution is advised if ritonavir must be used concurrently with medications that undergo metabolism by CYP450 2D6, particularly those with a narrow therapeutic range.', NULL, 'Caution is advised if ritonavir must be used concurrently with medications that undergo metabolism by CYP450 2D6, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever ritonavir is added to or withdrawn from therapy.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/101048/', '[1] "Product Information. Norvir (ritonavir)." Abbott Pharmaceutical, Abbott Park, IL.[2] "Product Information. Norvir (ritonavir)." Abbott Pharmaceutical, Abbott Park, IL.[3] "Product Information. Viekira Pak (dasabuvir/ombitasvir/paritaprevir/ritonavir)." AbbVie US LLC, North Chicago, IL.', 1767365206);
