INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (100967, 'Ritonavir', 'Quetiapine', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of quetiapine, which is primarily metabolized by the isoenzyme.', NULL, 'The dosage of quetiapine should be reduced when prescribed with potent CYP450 3A4 inhibitors such as itraconazole, ketoconazole, posaconazole, voriconazole, conivaptan, nefazodone, cobicistat, delavirdine, protease inhibitors, and ketolide and certain macrolide antibiotics.', NULL, 'The dosage of quetiapine should be reduced when prescribed with potent CYP450 3A4 inhibitors such as itraconazole, ketoconazole, posaconazole, voriconazole, conivaptan, nefazodone, cobicistat, delavirdine, protease inhibitors, and ketolide and certain macrolide antibiotics. The product labeling recommends a reduction to one-sixth of the original dosage. Following discontinuation of the CYP450 3A4 inhibitor, the dosage of quetiapine should be increased by 6-fold.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/101081/', '[1] DeVane CL, Nemeroff CB "Clinical pharmacokinetics of quetiapine - An atypical antipsychotic." Clin Pharmacokinet 40 (2001): 509-22[2] Hantson P, Di Fazio V, Wallemacq P "Toxicokinetic interaction between quetiapine and antiretroviral therapy following quetiapine overdose." Drug Metab Lett 4 (2010): 7-8[3] Geraci MJ, McCoy SL, Crum PM, Patel RA "Antipsychotic-induced priapism in an HIV patient: a cytochrome P450-mediated drug interaction." Int J Emerg Med 3 (2010): 81-4[4] Spina E, de Leon J "Metabolic drug interactions with newer antipsychotics: a comparative review." Basic Clin Pharmacol Toxicol 100 (2007): 4-22[5] Grimm SW, Richtand NM, Winter HR, Stams KR, Reele SB "Effects of cytochrome P450 3A modulators ketoconazole and carbamazepine on quetiapine pharmacokinetics." Br J Clin Pharmacol 61 (2006): 58-69[6] Urichuk L, Prior TI, Dursun S, Baker G "Metabolism of atypical antipsychotics: involvement of cytochrome p450 enzymes and relevance for drug-drug interactions." Curr Drug Metab 9 (2008): 410-8[7] Schulz-Du Bois C, Schulz-Du Bois AC, Bewig B, et al. "Major increase of quetiapine steady-state plasma concentration following co-administration of clarithromycin: confirmation of the pharmacokinetic interaction potential of quetiapine." Pharmacopsychiatry 41 (2008): 258-9[8] Spina E, Scordo MG, D''Arrigo C "Metabolic drug interactions with new psychotropic agents." Fundam Clin Pharmacol 17 (2003): 517-38[9] "Product Information. Seroquel (quetiapine)." Zeneca Pharmaceuticals, Wilmington, DE.', 1767365206);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (100968, 'Ritonavir', 'Quinidine', 'Major', 'Coadministration with ritonavir may significantly increase the plasma concentrations of some antiarrhythmic agents such as amiodarone, bepridil, flecainide, propafenone, and quinidine. The proposed mechanism is ritonavir inhibition of CYP450 3A4 and/or 2D6, the isoenzymes responsible for the metabolic clearance of these agents. Although clinical data are lacking, the interaction could conceivably lead to serious and/or life-threatening reactions including QT interval prolongation and ventricular arrhythmias such as ventricular tachycardia and torsade de pointes.', NULL, 'The use of ritonavir with amiodarone, bepridil, flecainide, propafenone, or quinidine is considered contraindicated.', NULL, 'The use of ritonavir with amiodarone, bepridil, flecainide, propafenone, or quinidine is considered contraindicated.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/101082/', '[1] "Product Information. Norvir (ritonavir)." Abbott Pharmaceutical, Abbott Park, IL.[2] Pharmaceutical Society of Australia "APPGuide online. Australian prescription products guide online. Available from: URL: http://www.appco.com.au/appguide/default.asp." ([2006]):[3] Cerner Multum, Inc. "Australian Product Information." O 0[4] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[5] Mangum EM, Graham KK "Lopinavir-Ritonavir: a new protease inhibitor." Pharmacotherapy 21 (2001): 1352-63[6] Canadian Pharmacists Association "e-CPS. Available from: URL: http://www.pharmacists.ca/function/Subscriptions/ecps.cfm?link=eCPS_quikLink."[7] Yamreudeewong W, DeBisschop M, Martin