INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (100978, 'Ritonavir', 'Ribociclib', 'Major', 'Coadministration with potent CYP450 3A4 inhibitors may increase the plasma concentrations and the risk of adverse effects of ribociclib, which is a substrate of the isoenzyme. The risk of adverse effects such as infections, neutropenia, leucopenia, anemia, thrombocytopenia, nausea, vomiting, diarrhea, stomatitis, anorexia, alopecia, fatigue, headache, and abnormal liver function may be increased.', NULL, 'Concomitant use of ribociclib with potent CYP450 3A4 inhibitors should generally be avoided.', NULL, 'Concomitant use of ribociclib with potent CYP450 3A4 inhibitors should generally be avoided. Alternative agents with no or minimal CYP450 3A4 inhibitory potential are recommended whenever possible. If no alternatives exist, the dose of ribociclib should be reduced to 400 mg once daily. Patients receiving concomitant treatment with a CYP450 3A4 inhibitor should be monitored for the development of ribociclib-related adverse effects. Following discontinuation of the potent CYP450 3A4 inhibitor, the ribociclib dosage should be returned (after at least 5 half-lives of the inhibitor) to that used prior to initiation of the inhibitor.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/101092/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Product Information. Kisqali (ribociclib)." Novartis Pharmaceuticals, East Hanover, NJ.[3] "Product Information. Kisqali (ribociclib)." Novartis Pharmaceuticals, East Hanover, NJ.', 1767365206);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (100979, 'Ritonavir', 'Rifabutin', 'Major', 'Coadministration with ritonavir may significantly increase the plasma concentrations of rifabutin and its pharmacologically active 25-O-desacetyl metabolite. The mechanism is ritonavir inhibition of CYP450 3A4, the isoenzyme responsible for the metabolic clearance of rifabutin and 25-O-desacetylrifabutin.', NULL, 'To minimize the risk of rifabutin toxicity, the manufacturers suggest that rifabutin dosage be reduced to 150 mg every other day or three times per week in patients treated with ritonavir.', NULL, 'To minimize the risk of rifabutin toxicity, the manufacturers suggest that rifabutin dosage be reduced to 150 mg every other day or three times per week in patients treated with ritonavir. More recently, U.S. HIV treatment guidelines and some infectious disease experts have been recommending a rifabutin dosage of 150 mg once daily or 300 mg three times per week when prescribed with ritonavir-boosted protease inhibitor regimens, as lower dosages of rifabutin have been associated with potentially subtherapeutic plasma levels in some studies. Therapeutic drug monitoring for rifabutin is advisable. Patients should be monitored closely for adverse effects of rifabutin, and a complete blood count should be performed at least weekly and as clinically indicated to monitor for development of neutropenia. Further dosage adjustments should be guided by therapeutic response and patient tolerance.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/101093/', '[1] "Product Information. Norvir (ritonavir)." Abbott Pharmaceutical, Abbott Park, IL.[2] Jenny-Avi