INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (100984, 'Ritonavir', 'Riociguat', 'Major', 'Coadministration with drugs that are both potent multi-pathway CYP450 and P-glycoprotein/breast cancer resistant protein (P-gp/BCRP) inhibitors may significantly increase the plasma concentrations of riociguat, which is primarily metabolized by CYP450 1A1, 3A, 2C8 and 2J2, and also a substrate of the P-gp/BCRP efflux transporter. Increased levels of riociguat may increase the risk for hypotension.', NULL, 'Caution is advised if riociguat is prescribed in combination with potent, dual multi-pathway CYP450 and P-gp/BCRP inhibitors such as itraconazole, ketoconazole, and some HIV protease inhibitors.', NULL, 'Caution is advised if riociguat is prescribed in combination with potent, dual multi-pathway CYP450 and P-gp/BCRP inhibitors such as itraconazole, ketoconazole, and some HIV protease inhibitors. Some authorities recommend avoiding concomitant use of riociguat during and for 2 weeks after treatment with itraconazole. However, if concomitant use is necessary, a starting dosage of riociguat 0.5 mg three times a day should be considered, and patients should be monitored for signs and symptoms of hypotension.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/101098/', '[1] "Product Information. Sporanox (itraconazole)." Janssen Pharmaceutica, Titusville, NJ.[2] "Product Information. Adempas (riociguat)." Bayer Pharmaceutical Inc, West Haven, CT.[3] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[4] Cerner Multum, Inc. "Australian Product Information." O 0[5] "Product Information. Adempas (riociguat)." Bayer Pharmaceutical Inc, West 