INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (100994, 'Rosuvastatin', 'Lopinavir', 'Major', 'Coadministration with protease inhibitors may significantly increase the plasma concentrations of rosuvastatin. The mechanism may involve inhibition of OATP1B1-mediated hepatic uptake and/or BCRP-mediated intestinal and hepatobiliary efflux. High levels of HMG-CoA reductase inhibitory activity in plasma is associated with an increased risk of musculoskeletal toxicity. Myopathy manifested as muscle pain and/or weakness associated with grossly elevated creatine kinase exceeding ten times the upper limit of normal has been reported occasionally. Rhabdomyolysis has also occurred rarely, which may be accompanied by acute renal failure secondary to myoglobinuria and may result in death.', NULL, 'The benefits of using rosuvastatin with protease inhibitors should be carefully weighed against the potentially increased risk of myopathy including rhabdomyolysis.', NULL, 'The benefits of using rosuvastatin with protease inhibitors should be carefully weighed against the potentially increased risk of myopathy including rhabdomyolysis. If coadministration is required, rosuvastatin should be initiated at the lowest effective dosage and not exceed 10 mg per day when prescribed with lopinavir-ritonavir, atazanavir-ritonavir, atazanavir-cobicistat, or ombitasvir/paritaprevir/ritonavir plus dasabuvir. The dosage of rosuvastatin should be limited to 20 mg once a day when used with darunavir-cobicistat. Alternatively, a different HMG-CoA reductase inhibitor such as fluvastatin may be considered. All patients receiving statin therapy should be advised to promptly report any unexplained muscle pain, tenderness or weakness, particularly if accompanied by fever, malaise and/or dark colored urine. Therapy should be discontinued if creatine kinase is markedly elevated in the absence of strenuous exercise or if myopathy is otherwise suspected or diagnosed.', 'Metabolism, Excretion', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/101108/', '[1] "Product Information. Norvir (ritonavir)." Abbott Pharmaceutical, Abbott Park, IL.[2] "Product Information. Aptivus (tipranavir)." Boehringer-Ingelheim, Ridgefield, CT.[3] Neuvonen PJ, Niemi M, Backman JT "Drug interactions with lipid-lowering drugs: Mechanisms and clinical relevance." Clin Pharmacol Ther 80 (2006): 565-81[4] "Product Information. Viekira Pak (dasabuvir/ombitasvir/paritaprevir/ritonavir)." AbbVie US LLC, North Chicago, IL.[5] "Product Information. Evotaz (atazanavir-cobicistat)." Bristol-Myers Squibb, Princeton, NJ.[6] "Product Information. Reyataz (atazanavir)." Bristol-Myers Squibb, Princeton, NJ.[7] "Product Information. Invirase (saquinavir)." Roche Laboratories, Nutley, NJ.[8] "Product Information. Crestor (rosuvastatin)." AstraZeneca Pharma Inc, Mississauga, ON.[9] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[10] "Product Information. Kaletra (lopinavir-ritonavir)" Abbott Pharmaceutical, Abbott Park, IL.[11] Hirano M, Maeda K, Shitara Y, Sugiyama Y "Drug-drug interaction between pitavastatin and various drugs via OATP1B1." Drug Metab Dispos 34 (2006): 1229-36[12] Cerner Multum, Inc. "Australian Product Information." O 0[13] "Product Information. Lexiva (fosamprenavir)." GlaxoSmithKline, Research Triangle Park, NC.[14] "Product Information. Prezista (darunavir)." Ortho Biotech Inc, Bridgewater, NJ.[15] "Product Information. Crixivan (indinavir)." Merck & Co, Inc, West Point, PA.[16] Demarles D, Gillotin C, Bonaventure-Paci S, Vincent I, Fosse S, Taburet AM "Single-dose pharmacokinetics of amprenavir coadministered with grapefruit juice." Antimicrob Agents Chemother 46 (2002): 1589-1590[17] "Product Information. Agenerase (amprenavir)." Glaxo Wellcome, Research Triangle Pk, NC.', 1767365206);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (100995, 'Rucaparib', 'Ritonavir', 'Moderate', 'Coadministration with potent inhibitors of CYP450 3A4 may increase the plasma concentrations and the risk of adverse effects of rucaparib, which has been shown in vitro to be a substrate of the isoenzyme. However, the effects on the pharmacokinetics of rucaparib have not been evaluated in vivo.', NULL, 'Caution is advised if rucaparib is used concomitantly with potent CYP450 3A4 inhibitors.', NULL, 'Caution is advised if rucaparib is used concomitantly with potent CYP450 3A4 inhibitors. Dosage adjustments as well as clinical and laboratory monitoring should be considered whenever a potent CYP450 3A4 inhibitor is added to or withdrawn from therapy with rucaparib.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/101109/', '[1] EMA. European Medicines Agency. European Union "EMA - List of medicines under additional monitoring. Available from: URL: http://www.ema.europa.eu/ema/index.jsp?curl=pages/regulation/document_listing/document_listing_000366.jsp&mid=WC0b01ac058067c852" ([2013 - ]):', 1767365206);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (100996, 'Rufinamide', 'Ritonavir', 'Moderate', 'Coadministration with drugs that are inducers of CYP450 3A4 may decrease the plasma concentrations of protease inhibitors (PIs), which are primarily metabolized by the isoenzyme.', NULL, 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, protease inhibitors should be used cautiously with agents that induce CYP450 3A4, particularly if only one PI is used in the antiretroviral regimen.', NULL, 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, protease inhibitors should be used cautiously with agents that induce CYP450 3A4, particularly if only one PI is used in the antiretroviral regimen. Coadministration of atazanavir without ritonavir and carbamazepine, phenobarbital, or phenytoin is not recommended. Antiretroviral response should be monitored more closely whenever a CYP450 3A4 inducer is added to or withdrawn from therapy, and the antiretroviral regimen adjusted as necessary.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/101110/', '[1] Sommadossi JP "HIV protease inhibitors: pharmacologic and metabolic distinctions." AIDS 13 (1999): s29-40[2] Durant J, Clevenbergh P, Garraffo R, Halfon P, Icard S, DelGiudice P, Montagne N, Schapiro JM, Dellamonica P "Importance of protease inhibitor plasma levels in HIV-infected patients treated with genotypic-guided therapy: pharmacological data from the Viradapt Study." Aids 14 (2000): 1333-9[3] "Product Information. Reyataz (atazanavir)." Bristol-Myers Squibb, Princeton, NJ.[4] "Product Information. Aptivus (tipranavir)." Boehringer-Ingelheim, Ridgefield, CT.[5] Acosta EP, Henry K, Baken L, Page LM, Fletcher CV "Indinavir concentrations and antiviral effect." Pharmacotherapy 19 (1999): 708-12[6] Canadian Pharmacists Association "e-CPS. Available from: URL: http://www.pharmacists.ca/function/Subscriptions/ecps.cfm?link=eCPS_quikLink."[7] Liedtke MD, Lockhart SM, Rathbun RC "Anticonvulsant and antiretroviral interactions." Ann Pharmacother 38 (2004): 482-9[8] Brooks J, Daily J, Schwamm L "Protease inhibitors and anticonvulsants." AIDS Clin Care 9 (1997): 87,90[9] "Product Information. Invirase (saquinavir)." Roche Laboratories, Nutley, NJ.[10] "Product Information. Crixivan (indinavir)." Merck & Co, Inc, West Point, PA.[11] "Product Information. Fortovase (saquinavir)" Roche Laboratories, Nutley, NJ.[12] Barry M, Gibbons S, Back D, Mulcahy F "Protease inhibitors in patients with HIV disease. Clinically important pharmacokinetic considerations." Clin Pharmacokinet 32 (1997): 194-209[13] Hugen PWH, Burger DM, Brinkman K, terHofstede HJM, Schuurman R, Koopmans PP, Hekster YA "Carbamazepine-indinavir interaction causes antiretroviral therapy failure." Ann Pharmacother 34 (2000): 465-70[14] "Product Information. Lexiva (fosamprenavir)." GlaxoSmithKline, Research Triangle Park, NC.[15] "Product Information. Agenerase (amprenavir)." Glaxo Wellcome, Research Triangle Pk, NC.[16] "Product Information. Prezista (darunavir)." Ortho Biotech Inc, Bridgewater, NJ.[17] "Product Information. Viracept (nelfinavir)." Agouron Pharma Inc, La Jolla, CA.[18] Skillman TG, Feldman JM "The pharmacology of sulfonylureas." Am J Med 70 (1981): 361-72[19] "Product Information. Diabinese (chlorpropamide)." Pfizer US Pharmaceuticals, New York, NY.[20] "Product Information. Diabeta (glyburide)." Hoechst Marion-Roussel Inc, Kansas City, MO.[21] Jerntorp P, Almer LO, Holin H, et al "Plasma chlorpropamide: a critical factor in chlo