INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1384066, 'Chloramphenicol', 'Ceftriaxone', 'Moderate', 'Limited, primarily in vitro, data suggest that chloramphenicol may antagonize the bactericidal activity of cephalosporins against certain clinical isolates of gram-negative rods, group B streptococci, and Staphylococcus aureus. This antagonism appears to occur against strains for which chloramphenicol is bacteriostatic, and has been demonstrated with cefoperazone, cefotaxime, and ceftriaxone. The proposed mechanism is inhibition of protein synthesis by chloramphenicol, resulting in less protein substrate for cephalosporins to act on as inhibitors of bacterial cell wall synthesis.', 'DDInter', 1767317475, 'The manufacturers recommend to avoid concomitant use . However, if concurrent administration cannot be avoided, the possibility of antagonism should be considered, and patients should be monitored for altered therapeutic effect.', 'Antagonism', 'The manufacturers recommend to avoid concomitant use.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1384067, 'Ceftriaxone', 'Vibrio cholerae CVD 103-HgR strain live antigen (live)', 'Major', 'INTERVAL: The use of live, attenuated cholera vaccine with systemic antibiotics may result in a diminished immunologic response to the vaccine. Some antibiotics may be active against the vaccine strain of Vibrio cholerae, thereby preventing a sufficient degree of multiplication to occur in order to induce a protective immune response.', 'DDInter', 1767317475, 'Live, attenuated cholera vaccine should not be administered during or for at least 14 days after treatment with systemic antibiotics.', 'Antagonism', 'Live, attenuated cholera vaccine should not be administered during or for at least 14 days after treatment with systemic antibiotics.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1384068, 'Cyclosporine', 'Ceftriaxone', 'Moderate', 'Limited data suggest that ceftriaxone may increase cyclosporine serum concentrations. Risk of cyclosporine-induced neurotoxicity and nephrotoxicity may be increased. The mechanism is unknown.', 'DDInter', 1767317475, 'If cyclosporine and ceftriaxone are used together, cyclosporine levels and renal function, including serum creatinine, should be carefully monitored. Cyclosporine dosage should be adjusted as needed. Patients should be advised to notify their physician if they experience nausea, vomiting, diarrhea, abdominal pain, dizziness, fatigue, or headache.', 'Synergism', 'If cyclosporine and ceftriaxone are used together, cyclosporine levels and renal function, including serum creatinine, should be carefully monitored.', NULL, NULL, 'Moderate', 'pharmacodynamic');
