INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1384101, 'Disulfiram', 'Cefuroxime', 'Moderate', 'An isolated case report suggests that the disulfiram reaction can occur during coadministration of cefuroxime axetil. Because the axetil moiety is metabolized to acetaldehyde in vivo, the reaction was presumably caused by an accumulation of acetaldehyde in the presence of disulfiram, which blocks the conversion of acetaldehyde to acetate. High levels of acetaldehyde can produce an unpleasant physiologic response referred to as the ''disulfiram reaction''. Symptoms include flushing, throbbing in head and neck, throbbing headache, respiratory difficulty, nausea, vomiting, sweating, thirst, chest pain, palpitation, dyspnea, hyperventilation, tachycardia, hypotension, syncope, weakness, vertigo, blurred vision, and confusion.', 'DDInter', 1767317475, 'Until more information is available, the possibility of a disulfiram reaction should be considered when prescribing cefuroxime axetil to patients treated with disulfiram.', 'Others', 'Until more information is available, the possibility of a disulfiram reaction should be considered when prescribing cefuroxime axetil to patients treated with disulfiram.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1384102, 'Entecavir', 'Cefuroxime', 'Moderate', 'Coadministration of entecavir with another drug that is also eliminated by active tubular secretion may result in increased plasma concentrations of one or both drugs due to competitive inhibition of transporters in the renal tubules. Drugs (and/or their metabolites) that are thought to undergo active tubular secretion include acyclovir, allopurinol, aminosalicylic acid, cidofovir, cimetidine, creatine, dyphylline, famciclovir, famotidine, flecainide, ganciclovir, levetiracetam, metformin, methotrexate, midodrine, mycophenolic acid, oseltamivir, pralatrexate, probenecid, procainamide, quinidine, ranitidine, tenofovir, triamterene, trimethoprim, valacyclovir, valganciclovir, zalcitabine, zidovudine, and many of the beta-lactam and quinolone antibiotics.', 'DDInter', 1767317475, 'Patients receiving entecavir with another drug that undergoes active tubular secretion should be monitored for excessive pharmacologic effects of both drugs, and the dosages adjusted as necessary.', 'Excretion', 'Patients receiving entecavir with another drug that undergoes active tubular secretion should be monitored for excessive pharmacologic effects of both drugs, and the dosages adjusted as necessary.', NULL, NULL, 'Moderate', 'pharmacodynamic');
