INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1384104, 'Etacrynic acid', 'Cefuroxime', 'Moderate', 'Limited data suggest that furosemide and possibly other loop diuretics may potentiate the nephrotoxicity of some cephalosporins. The exact mechanism of interaction is unknown, although furosemide has been shown to increase the plasma concentrations and/or reduce the clearance of several cephalosporins such as cephaloridine and ceftazidime.', 'DDInter', 1767317475, 'Although data are primarily limited to cephaloridine, which is no longer commercially marketed, caution may be advisable in patients receiving a loop diuretic in combination with other cephalosporins. Renal function should be monitored, particularly when high dosages are used or when these medications are administered in the elderly or patients with preexisting renal impairment.', 'Synergism', 'Although data are primarily limited to cephaloridine, which is no longer commercially marketed, caution may be advisable in patients receiving a loop diuretic in combination with other cephalosporins.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1384105, 'Gentamicin', 'Cefuroxime', 'Moderate', 'Coadministration of aminoglycosides and cephalosporins may increase the risk of nephrotoxicity. An increased incidence of nephrotoxicity has been reported during concomitant use of aminoglycosides and some, mostly older cephalosporins (e.g., cefaloridine, cefamandole, cefazolin, cefotaxime, cefoxitin, ceftazidime, cefuroxime, cephalothin, ceftriaxone). The risk may be greatest in the elderly or patients with preexisting renal impairment, when large doses are used, and during prolonged treatment.', 'DDInter', 1767317475, 'The lowest effective dosages of aminoglycosides and cephalosporins should be used when they are prescribed in combination. Renal function should be monitored closely. The same precaution may be applicable when aminoglycosides are administered via irrigation, intrapleurally, intraperitoneally or orally, since aminoglycosides can be systemically absorbed via these routes; however, clinical data are lacking.', 'Synergism', 'The lowest effective dosages of aminoglycosides and cephalosporins should be used when they are prescribed in combination.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1384106, 'Givosiran', 'Cefuroxime', 'Moderate', 'The concurrent or sequential use of two or more potentially nephrotoxic or neurotoxic drugs may increase the risk of toxicity. Patients who are elderly, debilitated, dehydrated, or who have preexisting renal dysfunction may have a higher risk of developing adverse reactions.', 'DDInter', 1767317475, 'Close clinical and laboratory monitoring of renal and neurologic function and drug serum concentrations (if clinically appropriate) is recommended.', 'Synergism', 'Close clinical and laboratory monitoring of renal and neurologic function and drug serum concentrations (if clinically appropriate) is recommended.', NULL, NULL, 'Moderate', 'pharmacodynamic');
