INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1384145, 'Enzalutamide', 'Celecoxib', 'Moderate', 'Coadministration with enzalutamide may decrease the plasma concentrations of drugs that are substrates of CYP450 3A4, 2C9, 2C19, and/or P-glycoprotein (P-gp) efflux transporter. The proposed mechanism involves accelerated clearance via these routes due to enzalutamide-mediated induction. The resulting plasma concentrations will depend on the sensitivity of the affected drugs to these isoenzymes.', 'DDInter', 1767317475, 'Caution is advised when enzalutamide is used concomitantly with drugs that are substrates of CYP450 3A4, 2C9, 2C19, and/or P-gp, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever enzalutamide is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised when enzalutamide is used concomitantly with drugs that are substrates of CYP450 3A4, 2C9, 2C19, and/or P-gp, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1384146, 'Epoprostenol', 'Celecoxib', 'Moderate', 'Anticoagulants, antiplatelet agents, and thrombolytics may potentiate the risk of gastrointestinal bleeding complications associated with the use of nonsteroidal anti-inflammatory drugs (NSAIDs) including COX-2 inhibitors.', 'DDInter', 1767317475, 'Although COX-2 inhibitors are often preferred over traditional, nonselective NSAIDs in patients who have increased risk for gastrointestinal toxicity, caution is advised during coadministration with anticoagulants or antiplatelet agents. Gastroprotective agents such as H2-receptor antagonists or proton pump inhibitors may be appropriate in high risk patients.', 'Synergism', 'Although COX-2 inhibitors are often preferred over traditional, nonselective NSAIDs in patients who have increased risk for gastrointestinal toxicity, caution is advised during coadministration with anticoagulants or antiplatelet agents.', NULL, NULL, 'Moderate', 'pharmacodynamic');
