INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1384239, 'Cilostazol', 'Cenobamate', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 and/or 2C19 may increase the plasma concentrations of cilostazol and or its pharmacologically active metabolites, which are substrates of these isoenzymes. The possibility of prolonged and/or increased pharmacologic effects of cilostazol should be considered.', 'DDInter', 1767317475, 'Close clinical and laboratory monitoring is advised whenever a CYP450 3A4 and/or 2C19 inhibitor is added to or withdrawn from cilostazol therapy, and the dosage adjusted as necessary. Patients should be advised to contact their physician if they experience adverse effects of cilostazol such as dizziness, nausea, diarrhea, bleeding, or irregular heartbeat.', 'Metabolism', 'Close clinical and laboratory monitoring is advised whenever a CYP450 3A4 and/or 2C19 inhibitor is added to or withdrawn from cilostazol therapy, and the dosage adjusted as necessary.', NULL, NULL, 'Moderate', 'pharmacodynamic');
