INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1384279, 'Docetaxel', 'Cenobamate', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of docetaxel, which is a substrate of the isoenzyme. Rifampin and hyperforin, a component of St. John''s wort, have been shown to significantly induce the in vitro metabolism of docetaxel in human hepatocyte cultures.', 'DDInter', 1767317475, 'Coadministration of docetaxel with potent CYP450 3A4 inducers such as carbamazepine, dexamethasone, enzalutamide, phenobarbital, phenytoin, rifamycins, and St. John''s wort should generally be avoided unless the benefit outweighs the potential risk of docetaxel underexposure. Alternative agents with no or minimal CYP450 3A4 induction potential are recommended whenever possible. If concomitant use is required, close monitoring for signs of reduced therapeutic efficacy is advised. Other known inducers include aminoglutethimide, barbiturates, bexarotene, bosentan, dabrafenib, efavirenz, etravirine, nafcillin, nevirapine, somatrem, somatropin, and various other anticonvulsants, although the extent to which they interact with docetaxel is unknown.', 'Metabolism', 'Coadministration of docetaxel with potent CYP450 3A4 inducers such as carbamazepine, dexamethasone, enzalutamide, phenobarbital, phenytoin, rifamycins, and St.', NULL, NULL, 'Moderate', 'pharmacodynamic');
