INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1384294, 'Elagolix', 'Cenobamate', 'Moderate', 'Coadministration with inducers of CYP450 3A4 and/or P-glycoprotein (P-gp) may decrease the plasma concentrations of elagolix, which is a substrate of both the metabolic isoenzyme and efflux transporter.', 'DDInter', 1767317475, 'The potential for diminished pharmacologic effects of elagolix should be considered during coadministration with CYP450 3A4 and/or P-gp inducers. Alternative treatments or a dose adjustment of elagolix may be required if an interaction is suspected.', 'Metabolism', 'The potential for diminished pharmacologic effects of elagolix should be considered during coadministration with CYP450 3A4 and/or P-gp inducers.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1384295, 'Eliglustat', 'Cenobamate', 'Moderate', 'Coadministration with inducers of CYP450 3A4 and/or P-glycoprotein (P-gp) may decrease the plasma concentrations of eliglustat, which is primarily metabolized by CYP450 2D6 and, to a lesser extent, CYP450 3A4. Eliglustat is also a substrate of P-gp efflux transporter.', 'DDInter', 1767317475, 'Patients receiving eliglustat with CYP450 3A4 inducers should be monitored for potentially diminished pharmacologic response to eliglustat. Concomitant use should be avoided if an interaction is suspected.', 'Metabolism', 'Patients receiving eliglustat with CYP450 3A4 inducers should be monitored for potentially diminished pharmacologic response to eliglustat.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1384296, 'Elvitegravir', 'Cenobamate', 'Moderate', 'Coadministration with inducers of CYP450 3A4 isoenzymes may reduce the plasma concentrations of elvitegravir, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317475, 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, the manufacturers of elvitegravir single-ingredient and combination products recommend avoiding concomitant use. Alternative treatment combinations that do not include metabolic inducers should be considered whenever possible.', 'Metabolism', 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, the manufacturers of elvitegravir single-ingredient and combination products recommend avoiding concomitant use.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1384297, 'Encorafenib', 'Cenobamate', 'Major', 'Coadministration with potent and moderate inducers of CYP450 3A4 may decrease the plasma concentrations of encorafenib, which is primarily metabolized by the isoenzyme. The extent to which CYP450 3A4 inducers may affect encorafenib exposure has not been studied.', 'DDInter', 1767317475, 'Concomitant use of encorafenib with potent and moderate CYP450 3A4 inducers should generally be avoided due to the potential for reduced efficacy.', 'Metabolism', 'Concomitant use of encorafenib with potent and moderate CYP450 3A4 inducers should generally be avoided due to the potential for reduced efficacy.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1384298, 'Entacapone', 'Cenobamate', 'Moderate', 'Central nervous system- and/or respiratory-depressant effects may be additively or synergistically increased in patients taking multiple drugs that cause these effects, especially in elderly or debilitated patients.', 'DDInter', 1767317475, 'During concomitant use of these drugs, patients should be monitored for potentially excessive or prolonged CNS and respiratory depression. Cautious dosage titration may be required, particularly at treatment initiation. Ambulatory patients should be counseled to avoid hazardous activities requiring mental alertness and motor coordination.', 'Synergism', 'During concomitant use of these drugs, patients should be monitored for potentially excessive or prolonged CNS and respiratory depression.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1384299, 'Entrectinib', 'Cenobamate', 'Major', 'Coadministration with potent or moderate inducers of CYP450 3A4 may significantly decrease the plasma concentrations of entrectinib, which is a substrate of the isoenzyme.', 'DDInter', 1767317475, 'Concomitant use of entrectinib with potent or moderate CYP450 3A4 inducers should generally be avoided.', 'Metabolism', 'Concomitant use of entrectinib with potent or moderate CYP450 3A4 inducers should generally be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1384300, 'Enzalutamide', 'Cenobamate', 'Moderate', 'Coadministration with inducers of CYP450 2C8 and/or 3A4 may decrease the plasma concentrations of enzalutamide, which is primarily metabolized by CYP450 2C8 to its pharmacologically active metabolite, N-desmethyl enzalutamide, and to a lesser extent by CYP450 3A4.', 'DDInter', 1767317475, 'Concomitant use of enzalutamide with potent CYP450 2C8 and/or 3A4 inducers such as carbamazepine, lumacaftor, mitotane, phenobarbital, phenytoin, primidone (partially metabolized to phenobarbital), rifamycins, and St. John''s wort should generally be avoided. Moderate inducers such as bosentan, efavirenz, etravirine, modafinil, nafcillin, and nevirapine should also be avoided if possible. The extent to which other, less potent CYP450 3A4 inducers may interact with enzalutamide is unknown. Caution is advised if they are used with enzalutamide.', 'Metabolism', 'Concomitant use of enzalutamide with potent CYP450 2C8 and/or 3A4 inducers such as carbamazepine, lumacaftor, mitotane, phenobarbital, phenytoin, primidone (partially metabolized to phenobarbital), rifamycins, and St.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1384301, 'Erdafitinib', 'Cenobamate', 'Major', 'Coadministration with moderate inducers of CYP450 2C9 or 3A4 may decrease the plasma concentrations and therapeutic effects of erdafitinib. The mechanism is induction of CYP450 2C9- or 3A4-mediated metabolism of erdafitinib, which has been shown to be metabolized by these isoenzymes.', 'DDInter', 1767317475, 'According to the manufacturer, if a moderate CYP450 2C9 or 3A4 inducer is coadministered at the start of erdafitinib therapy, the recommended dose of erdafitinib should be administered. If a moderate CYP450 2C9 or 3A4 inducer is coadministered after the initial dose increase period, the dose of erdafitinib may be increased up to 9 mg once daily based on serum phosphate levels and tolerability. When a moderate CYP450 2C9 or 3A4 inducer is discontinued, the current dose of erdafitinib may be continued in the absence of erdafitinib toxicity.', 'Metabolism', 'According to the manufacturer, if a moderate CYP450 2C9 or 3A4 inducer is coadministered at the start of erdafitinib therapy, the recommended dose of erdafitinib should be administered.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1384302, 'Ergometrine', 'Cenobamate', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of ergot alkaloids, which are substrates of the isoenzyme.', 'DDInter', 1767317475, 'The potential for diminished pharmacologic effects of ergot alkaloids should be considered during coadministration with CYP450 3A4 inducers. Alternative treatments may be required if an interaction is suspected.', 'Metabolism', 'The potential for diminished pharmacologic effects of ergot alkaloids should be considered during coadministration with CYP450 3A4 inducers.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1384303, 'Ergotamine', 'Cenobamate', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of ergot alkaloids, which are substrates of the isoenzyme.', 'DDInter', 1767317475, 'The potential for diminished pharmacologic effects of ergot alkaloids should be considered during coadministration with CYP450 3A4 inducers. Alternative treatments may be required if an interaction is suspected.', 'Metabolism', 'The potential for diminished pharmacologic effects of ergot alkaloids should be considered during coadministration with CYP450 3A4 inducers.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1384304, 'Erlotinib', 'Cenobamate', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of erlotinib, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317475, 'Alternative treatment lacking CYP450 3A4-inducing activity is recommended in patients receiving erlotinib. If alternative treatment is unavailable and a potent inducer (e.g., carbamazepine, enzalutamide, lumacaftor, mitotane, phenobarbital, phenytoin, primidone (partially metabolized to phenobarbital), rifamycins, St. John''s wort) is to be used for more than a few days, an increase in the dosage of erlotinib should be considered as tolerated at 2-week intervals. Close clinical and laboratory monitoring for altered efficacy and safety, including renal and liver functions and serum electrolytes, is recommended.', 'Metabolism', 'Alternative treatment lacking CYP450 3A4-inducing activity is recommended in patients receiving erlotinib.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1384305, 'Erythromycin', 'Cenobamate', 'Moderate', 'Coadministration with cenobamate may decrease the plasma concentrations and pharmacologic effects of CYP450 3A4 substrates. The proposed mechanism is cenobamate-mediated induction of the CYP450 3A4 isoenzyme.', 'DDInter', 1767317475, 'Caution is advised if cenobamate is used concomitantly with drugs that are substrates of CYP450 3A4, particularly those with a narrow therapeutic index. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever cenobamate is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised if cenobamate is used concomitantly with drugs that are substrates of CYP450 3A4, particularly those with a narrow therapeutic index.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1384306, 'Escitalopram', 'Cenobamate', 'Moderate', 'Coadministration with inhibitors of CYP450 2C19 may increase the plasma concentrations of escitalopram, which is primarily metabolized by the isoenzyme. High plasma levels of escitalopram may increase the risk of serious side effects such as QT prolongation, which may lead to arrhythmias including torsade de pointes and sudden death, as well as serotonin syndrome, which is a rare but potentially fatal condition thought to result from hyperstimulation of brainstem 5-HT1A and 2A receptors.', 'DDInter', 1767317475, 'Caution is advised if escitalopram must be used in combination with CYP450 2C19 inhibitors such as cimetidine, esomeprazole, etravirine, felbamate, fluconazole, lansoprazole, letrozole, modafinil, omeprazole, oxcarbazepine, ticlopidine, and voriconazole. Pharmacologic response to escitalopram should be monitored more closely whenever a CYP450 2C19 inhibitor is added to or withdrawn from therapy, and the escitalopram dosage adjusted as necessary. This may be particularly important in patients receiving escitalopram at the upper end of the dosage range. Patients should be advised to seek medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heartbeat, shortness of breath, or syncope. Patients should also be closely monitored for symptoms of the serotonin syndrome during treatment.', 'Metabolism', 'Caution is advised if escitalopram must be used in combination with CYP450 2C19 inhibitors such as cimetidine, esomeprazole, etravirine, felbamate, fluconazole, lansoprazole, letrozole, modafinil, omeprazole, oxcarbazepine, ticlopidine, and voriconazole.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1384307, 'Esketamine', 'Cenobamate', 'Moderate', 'Concomitant use of esketamine with central nervous system (CNS) depressants may increase sedation and impairment of attention, judgment, thinking, reaction speed, and psychomotor skills.', 'DDInter', 1767317475, 'Caution is advised and patients should be closely monitored during concomitant use of esketamine with CNS depressants. Due to the risk of delayed or prolonged sedation and other adverse effects, patients should be monitored for at least 2 hours after esketamine administration, followed by an assessment to determine when the patient is considered clinically stable and ready to leave the healthcare setting. Patients should be instructed not to engage in potentially hazardous activities, such as driving a motor vehicle or operating machinery, until the next day after a restful sleep.', 'Synergism', 'Caution is advised and patients should be closely monitored during concomitant use of esketamine with CNS depressants.', NULL, NULL, 'Moderate', 'pharmacodynamic');
