INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1384322, 'Everolimus', 'Cenobamate', 'Moderate', 'Coadministration with inducers of CYP450 3A4 and/or P-glycoprotein (P-gp) may decrease the blood concentrations and pharmacologic effects of everolimus.', 'DDInter', 1767317475, 'Some manufacturers recommend avoiding concomitant use of everolimus with moderate CYP450 3A4 and/or P-gp inducers such as bosentan, efavirenz, and nevirapine. If concomitant use is required, a dose adjustment of everolimus should be considered to achieve the recommended therapeutic range for the condition being treated. Everolimus whole blood trough levels should be closely monitored during treatment, particularly 2 weeks after a dose increase and 2 weeks after discontinuation of the inducer.', 'Metabolism', 'Some manufacturers recommend avoiding concomitant use of everolimus with moderate CYP450 3A4 and/or P-gp inducers such as bosentan, efavirenz, and nevirapine.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1384323, 'Exemestane', 'Cenobamate', 'Moderate', 'Coadministration with potent inducers of CYP450 3A4 may significantly decrease the plasma concentrations of exemestane, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317475, 'The manufacturer recommends increasing the dosage of exemestane to 50 mg once daily when used with potent CYP450 3A4 inducers such as carbamazepine, enzalutamide, lumacaftor, mitotane, phenobarbital, phenytoin, primidone (partially metabolized to phenobarbital), rifamycins, and St. John''s wort. However, it has also been suggested that suppression of estrogen levels is not affected by the interaction, thus dosage adjustment of exemestane is not required. The extent to which other, less potent CYP450 3A4 inducers may interact with exemestane is unknown. Caution is advised if they are used with exemestane.', 'Metabolism', 'The manufacturer recommends increasing the dosage of exemestane to 50 mg once daily when used with potent CYP450 3A4 inducers such as carbamazepine, enzalutamide, lumacaftor, mitotane, phenobarbital, phenytoin, primidone (partially metabolized to phenobarbital), rifamycins, and St.', NULL, NULL, 'Moderate', 'pharmacodynamic');
