INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1384355, 'Isradipine', 'Cenobamate', 'Moderate', 'Coadministration with cenobamate may decrease the plasma concentrations and pharmacologic effects of CYP450 3A4 substrates. The proposed mechanism is cenobamate-mediated induction of the CYP450 3A4 isoenzyme.', 'DDInter', 1767317475, 'Caution is advised if cenobamate is used concomitantly with drugs that are substrates of CYP450 3A4, particularly those with a narrow therapeutic index. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever cenobamate is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised if cenobamate is used concomitantly with drugs that are substrates of CYP450 3A4, particularly those with a narrow therapeutic index.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1384356, 'Istradefylline', 'Cenobamate', 'Moderate', 'Coadministration with moderate inducers of CYP450 3A4 may decrease the plasma concentrations of istradefylline, which is primarily metabolized by CYP450 3A4 and 1A1. No data are available regarding the effects that other, less potent CYP450 3A4 inducers will have on the pharmacokinetics of istradefylline.', 'DDInter', 1767317475, 'Caution is advised if istradefylline is used with moderate CYP450 3A4 inducers. Clinical monitoring of istradefylline should be considered whenever a moderate CYP450 3A4 inducer is added to or withdrawn from therapy. Patients should be monitored for diminished therapeutic effects.', 'Metabolism', 'Caution is advised if istradefylline is used with moderate CYP450 3A4 inducers.', NULL, NULL, 'Moderate', 'pharmacodynamic');
