INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1384569, 'Cenobamate', 'Ticagrelor', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of ticagrelor, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317475, 'Concomitant use of ticagrelor with CYP450 3A4 inducers should generally be avoided. Otherwise, caution is advised, and patients should be closely monitored for diminished clinical response to ticagrelor therapy. Alternative treatment may be required if an interaction is suspected.', 'Metabolism', 'Concomitant use of ticagrelor with CYP450 3A4 inducers should generally be avoided.', NULL, NULL, 'Moderate', 'pharmacodynamic');
