INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1384571, 'Cenobamate', 'Tipranavir', 'Moderate', 'Coadministration with drugs that are inducers of CYP450 3A4 may decrease the plasma concentrations of protease inhibitors (PIs), which are primarily metabolized by the isoenzyme.', 'DDInter', 1767317475, 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, protease inhibitors should be used cautiously with agents that induce CYP450 3A4, particularly if only one PI is used in the antiretroviral regimen. Coadministration of atazanavir without ritonavir and carbamazepine, phenobarbital, or phenytoin is not recommended. Antiretroviral response should be monitored more closely whenever a CYP450 3A4 inducer is added to or withdrawn from therapy, and the antiretroviral regimen adjusted as necessary.', 'Metabolism', 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, protease inhibitors should be used cautiously with agents that induce CYP450 3A4, particularly if only one PI is used in the antiretroviral regimen.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1384572, 'Cenobamate', 'Tizanidine', 'Moderate', 'The sedative effect of tizanidine may be potentiated by concomitant use of other agents with central nervous system (CNS) depressant effects. In addition, tizanidine and many of these agents (e.g., alcohol, anxiolytics, sedatives, hypnotics, antidepressants, antipsychotics, opioids, muscle relaxants) also can exhibit hypotensive effects, which may be additive during coadministration and may increase the risk of symptomatic hypotension and orthostasis, particularly during initiation of therapy or dose escalation. Tizanidine itself is a central alpha-2 adrenergic agonist. Pharmacologic studies have found tizanidine to possess between 1/10 to 1/50 of the potency of clonidine, a structurally similar agent, in lowering blood pressure.', 'DDInter', 1767317475, 'Caution is advised and more gradual dose titration may be appropriate when tizanidine is used with other substances that can cause CNS depression and/or hypotension, particularly in elderly or debilitated patients. Ambulatory patients should be counseled to avoid hazardous activities requiring complete mental alertness and motor coordination until they know how these agents affect them, and to avoid rising abruptly from a sitting or recumbent position. Patients should contact their physician if they experience dizziness, lightheadedness, syncope, orthostasis, or excessive CNS effects that interfere with their normal activities.', 'Synergism', 'Caution is advised and more gradual dose titration may be appropriate when tizanidine is used with other substances that can cause CNS depression and/or hypotension, particularly in elderly or debilitated patients.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1384573, 'Cenobamate', 'Tofacitinib', 'Moderate', 'Coadministration with moderate inducers of CYP450 3A4 may decrease the plasma concentrations of tofacitinib, which is primarily metabolized by the isoenzyme. The interaction may result in loss of or reduced clinical response; however, the extent to which these medications interact with tofacitinib has not been established.', 'DDInter', 1767317475, 'Caution is recommended if tofacitinib is to be used in combination with moderate CYP450 3A4 inducers. Alternative agents with no or minimal CYP450 3A4 induction potential are recommended when possible.', 'Metabolism', 'Caution is recommended if tofacitinib is to be used in combination with moderate CYP450 3A4 inducers.', NULL, NULL, 'Moderate', 'pharmacodynamic');
