INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (101063, 'Tolterodine', 'Ritonavir', 'Moderate', 'Coadministration with drugs that are inhibitors of CYP450 3A4 may increase the plasma concentrations of tolterodine, which is partially metabolized by the isoenzyme. The possibility of prolonged and/or increased pharmacologic effects of tolterodine should be considered. Although tolterodine is primarily metabolized by CYP450 2D6, there is some evidence that CYP450 3A4 may play a minor role, thus any alteration in its activity levels could conceivably affect the metabolism of tolterodine.', NULL, 'The manufacturer recommends a maximum tolterodine dosage of 1 mg twice daily in patients receiving concomitant CYP450 3A4 inhibitors.', NULL, 'The manufacturer recommends a maximum tolterodine dosage of 1 mg twice daily in patients receiving concomitant CYP450 3A4 inhibitors. Close clinical and laboratory monitoring is advised whenever a CYP450 3A4 inhibitor is added to or withdrawn from therapy. Patients should be advised to notify their physician if they experience an irregular heartbeat, severe blurry vision, difficulty urinating, dry mouth, headache, drowsiness, dizziness, or GI upset.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/101177/', '[1] "Product Information. Detrol (tolterodine)." Pharmacia and Upjohn, Kalamazoo, MI.[2] Brynne N, Forslund C, Hallen B, Gustafsson LL, Bertilsson L "Ketoconazole inhibits the metabolism of tolterodine in subjects with deficient CYP2D6 activity." Br J Clin Pharmacol 48 (1999): 564-72', 1767365206);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (101064, 'Tolvaptan', 'Ritonavir', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of vasopressin receptor antagonists conivaptan and tolvaptan, both of which are almost exclusively metabolized by the isoenzyme.', NULL, 'Concomitant use of conivaptan or tolvaptan with potent CYP450 3A4 inhibitors is considered contraindicated.', NULL, 'Concomitant use of conivaptan or tolvaptan with potent CYP450 3A4 inhibitors is considered contraindicated. Some authorities recommend avoiding concomitant use of conivaptan or tolvaptan during and for 2 weeks after treatment with itraconazole.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/101178/', '[1] Cerner Multum, Inc. "Australian Product Information." O 0[2] "Product Information. Sporanox (itraconazole)." Janssen Pharmaceutica, Titusville, NJ.[3] "Product Information. Samsca (tolvaptan)." Otsuka American Pharmaceuticals Inc, Rockville, MD.[4] "Product Information. Vaprisol (conivaptan)." Cumberland Pharmaceuticals Inc, Nashville, TN.[5] Wohlt PD, Zheng L, Gunderson S, Balzar SA, Johnson BD, Fish JT "Recommendations for the use of medications with continuous enteral nutrition." Am J Health Syst Pharm 66 (2009): 1438-67[6] "Product Information. VFEND (voriconazole)." Pfizer U.S. Pharmaceuticals, New York, NY.[7] "Product Information. Samsca (tolvaptan)." Otsuka American Pharmaceuticals Inc, Rockville, MD.', 1767365206);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (101065, 'Toremifene', 'Ritonavir', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of toremifene, which is primarily metabolized by the isoenzyme. Because toremifene is associated with dose- and concentration-dependent prolongation of the QT interval, increased levels may potentiate the risk of ventricular arrhythmias such as torsade de pointes and sudden death.', NULL, 'The use of toremifene in combination with potent CYP450 3A4 inhibitors such as itraconazole, ketoconazole, voriconazole, nefazodone, delavirdine, protease inhibitors, and ketolide and certain macrolide antibiotics should generally be avoided.', NULL, 'The use of toremifene in combination with potent CYP450 3A4 inhibitors such as itraconazole, ketoconazole, voriconazole, nefazodone, delavirdine, protease inhibitors, and ketolide and certain macrolide antibiotics should generally be avoided. If treatment with one of these agents is required, interruption of toremifene therapy should be considered. Otherwise, patients who require concomitant treatment should be closely monitored. In patients at increased risk, electrocardiograms (ECGs) should be obtained at baseline and as clinically indicated. Complete blood counts, electrolyte levels (calcium, magnesium, potassium), and liver function tests should also be obtained periodically. Patients should be advised to contact their physician if they experience vaginal bleeding or potential signs of blood clots such as chest pain, shortness of breath, sudden loss of vision, and pain, redness or swelling in an extremity. Patients should seek immediate medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, palpitations, or syncope.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/101179/', '[1] "Product Information. Fareston (toremifene)." Schering Laboratories, Kenilworth, NJ.[2] "Product Information. Fareston (toremifene)." Schering Laboratories, Kenilworth, NJ.[3] Therapeutic Research Faculty "Natural Medicines Comprehensive Database. Available from: URL: http://www.naturaldatabase.com." ([1995-2008...]):[4] Wohlt PD, Zheng L, Gunderson S, Balzar SA, Johnson BD, Fish JT "Recommendations for the use of medications with continuous enteral nutrition." Am J Health Syst Pharm 66 (2009): 1438-67[5] "Product Information. VFEND (voriconazole)." Pfizer U.S. Pharmaceuticals, New York, NY.', 1767365206);
