INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (101102, 'Voriconazole', 'Ritonavir', 'Major', 'Coadministration with ritonavir may significantly decrease or increase the plasma concentrations of voriconazole. The proposed mechanism may involve ritonavir-mediated induction of voriconazole metabolism via the CYP450 isoenzymes 2C19 and 2C9. Voriconazole is primarily metabolized via CYP450 2C19, and to a lesser extent, by CYP450 2C9 and 3A4.', NULL, 'The use of voriconazole in combination with high-dose ritonavir (400 mg or more every 12 hours) is considered contraindicated.', NULL, 'The use of voriconazole in combination with high-dose ritonavir (400 mg or more every 12 hours) is considered contraindicated. Coadministration of voriconazole and low-dose ritonavir (100 mg every 12 hours) should also be avoided, unless the benefit justifies the risk.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/101216/', '[1] Liu P, Foster G, Gandelman K, et al. "Steady-state pharmacokinetic and safety profiles of voriconazole and ritonavir in healthy male subjects." Antimicrob Agents Chemother 51 (2007): 3617-26[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] "Product Information. VFEND (voriconazole)." Pfizer U.S. Pharmaceuticals, New York, NY.[4] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[5] Cerner Multum, Inc. "Australian Product Information." O 0[6] "Product Information. Kaletra (lopinavir-ritonavir)" Abbott Pharmaceutical, Abbott Park, IL.[7] Canadian Pharmacists Association "e-CPS. Available from: URL: http://www.pharmacists.ca/function/Subscriptions/ecps.cfm?link=eCPS_quikLink."[8] Cerner Multum, Inc. "UK Sum