INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (101111, 'Zolpidem', 'Ritonavir', 'Minor', 'Use of drugs that inhibit the isoenzyme CYP450 3A4 may decrease the metabolism and increase the plasma levels of zolpidem. This can result in increased clinical effectiveness and risk of toxicity associated with zolpidem. Monitoring for signs and symptoms of altered zolpidem effect is recommended. Patients should be advised to notify their doctor if they experience nausea, vomiting, diarrhea, confusion, daytime sedation, dizziness, or unconsciousness. A reduction in zolpidem dosage may be necessary.', NULL, '-', NULL, '-', 'Metabolism', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/101225/', '[1] "Multum Information Services, Inc. Expert Review Panel"[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] Agencia Espaola de Medicamentos y Productos Sanitarios Healthcare "Centro de informacin online de medicamentos de la AEMPS - CIMA. Available from: URL: https://cima.aemps.es/cima/publico/home.html." ([2018]):[4] Cerner Multum, Inc. "Australian Product Information." O 0[5] "Product Information. Ambien (zolpidem)." sanofi-aventis, Bridgewater, NJ.[6] Yamreudeewong W, Henann NE, Fazio A, Lower DL, Cassidy TG "Drug-food interactions in clinical practice." J Fam Pract 40 (1995): 376-84[7] "Product Information. Kaletra (lopinavir-ritonavir)" Abbott Pharmaceutical, Abbott Park, IL.[8] "Product Information. Norvir (ritonavir)." Abbott Pharmaceutical, Abbott Park, IL.', 1767365206);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (101112, 'Zonisamide', 'Ritonavir', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of zonisamide, which is metabolized by the isoenzyme. While clinical data are lacking, the possibility of prolonged and/or increased pharmacologic effects of zonisamide should be considered.', NULL, 'Dosage adjustments as well as clinical and laboratory monitoring may be appropriate whenever a CYP450 3A4 inhibitor is added to or withdrawn from therapy.', NULL, 'Dosage adjustments as well as clinical and laboratory monitoring may be appropriate whenever a CYP450 3A4 inhibitor is added to or withdrawn from therapy. Patients should be advised to notify their physician if they experience symptoms such as severe drowsiness, confusion, loss of coordination, depression, aphasia, decreased sweating, fever, loss of seizure control, or slow pulse.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/101226/', '[1] Nakasa H, Nakamura H, Ono S, Tsutsui M, Kiuchi M, Ohmori S, Kitada M "Prediction of drug-drug interactions of zonisamide metabolism in humans from in vitro data." Eur J Clin Pharmacol 54 (1998): 177-83[2] "Product Information. Zonegran (zonisamide)" Elan Pharmaceuticals, S. San Francisco, CA.[3] Warrington SJ, Ankier SI, Turner P "Evaluation of possible interactions between ethanol and trazodone or amitriptyline." Neuropsychobiology 15 (1986): 31-7[4] "Product Information. Rexulti (brexpiprazole)." Otsuka American Pharmaceuticals Inc, Rockville, MD.[5] Gilman AG, Rall TW, Nies AS, Taylor P, eds. "Goodman and Gilman''s the Pharmacological Basis of Therapeutics. 8th ed." New York, NY: Pergamon Press Inc. (1990):[6] "Product Information. Fycompa (perampanel)." Eisai Inc, Teaneck, NJ.', 1767365206);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (101113, 'Acetohexamide', 'Metoprolol', 'Moderate', 'Beta-blockers may inhibit some of the normal physiologic response to hypoglycemia. Symptoms of hypoglycemia such as tremor and tachycardia may be absent, making it more difficult for patients to recognize an oncoming episode. Specifically, inhibition of catecholamine-mediated glycogenolysis and glucose mobilization in association with beta-blockade can potentiate insulin-induced hypoglycemia in diabetics and delay the recovery of normal blood glucose levels. Prolonged and severe hypoglycemia may occur, although these events have rarely been reported. Significant increases in blood pressure and bradycardia can also occur during hypoglycemia in diabetics treated with insulin and beta-blockers due to antagonism of epinephrine''s effect on beta-2 adrenergic receptors, which leads to unopposed alpha-adrenergic effects including vasoconstriction. Other effects reported with various beta-blockers include decreased glucose tolerance and decreased glucose-induced insulin secretion.', NULL, 'In general, cardioselective beta-blockers are considered safer than noncardioselective agents in the treatment of diabetic patients.', NULL, 'In general, cardioselective beta-blockers are considered safer than noncardioselective agents in the treatment of diabetic patients. Patients should be advised of the need for regular blood glucose monitoring and be aware that certain symptoms of hypoglycemia such as tremor and tachycardia may be masked. However, other symptoms such as headache, dizziness, drowsiness, confusion, nausea, hunger, weakness, and perspiration may be unaffected.', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/101227/', '[1] Zaman R, Kendall MJ, Biggs PI "The effect of acebutolol and propranolol on the hypoglycaemic action of glibenclamide." Br J Clin Pharmacol 13 (1982): 507-12[2] Shepherd AM, Lin M-S, Keeton TK "Hypoglycemia-induced hypertension in a diabetic patient on metoprolol." Ann Intern Med 94 (1981): 357-8[3] Viberti GC, Keen H, Bloom SR "Beta blockade and diabetes mellitus: effect of oxprenolol and metoprolol on the metabolic, cardiovascular, and hormonal response to insulin-induced hypoglycemia in normal subjects." Metabolism 29 (1980): 866-72[4] Viberti GC, Keen H, Bloom SR "Beta blockade and diabetes mellitus: effect of oxprenolol and metoprolol on the metabolic, cardiovascular, and hormonal response to insulin-induced hypoglycemia in insulin-dependent diabetics." Metabolism 29 (1980): 873-9[5] Ostman J "B-adrenergic blockade and diabetes mellitus." Acta Med Scand 672 (1983): 69-77[6] Groop L, Totterman KJ, Harno K, Gordin A "Influence of beta-blocking drugs on glucose metabolism in patients with non-insulin dependent diabetes mellitus." Acta Med Scand 211 (1982): 7-12[7] Sinclair AJ, Davies IB, Warrington SJ "Betaxolol and glucose-insulin relationships: studies in normal subjects taking glibenclamide or metformin." Br J Clin Pharmacol 30 (1990): 699-702[8] Popp DA, Tse TF, Shah SD, et al "Oral propranolol and metoprolol both impair glucose recovery from insulin-induced hypoglycemia in insulin-dependent diabetes mellitus." Diabetes Care 7 (1984): 243-7[9] Mann SJ, Krakoff LR "Hypertensive crisis caused by hypoglycemia and propranolol." Arch Intern Med 144 (1984): 2427-8[10] Deacon SP, Karunanayake A, Barnett D "Acebutolol, atenolol, and propranolol and metabolic responses to acute hypoglycaemia in diabetes." Br Med J 12 (1977): 1255-7[11] Pollare T, Lithell H, Selinus I, Berne C "Sensitivity to insulin during treatment with atenolol and metoprolol: a randomised, double blind study of effects on carbohydrate and lipoprotein metabolism in hypertensive patients." BMJ 298 (1989): 1152-7[12] Munroe WP, Rindone JP, Kershner RM "Systemic side effects associated with the ophthalmic administratiion of timolol." Drug Intell Clin Pharm 19 (1985): 85-9[13] Micossi P, Pollavini G, Raggi U, et al "Effects of metoprolol and propranolol on glucose tolerance and insulin secretion in diabetes mellitus." Horm Metab Res 16 (1984): 59-63[14] "New Zealand Committee on Adverse Drug Reactions. Ninth Annual Report." N Z Dent J 71 (1975): 28-32[15] Smith U "Beta blockade in diabetes." N Engl J Med 299 (1978): 1467[16] Newman RJ "Comparison of propranolol, metoprolol, and acebutolol on insulin-induced hypoglycaemia." Br Med J 2 (1976): 447-9[17] Skillman TG, Feldman JM "The pharmacology of sulfonylureas." Am J Med 70 (1981): 361-72[18] "Product Information. Diabinese (chlorpropamide)." Pfizer US Pharmaceuticals, New York, NY.[19] "Product Information. Diabeta (glyburide)." Hoechst Marion-Roussel Inc, Kansas City, MO.[20] Jerntorp P, Almer LO, Holin H, et al "Plasma chlorpropamide: a critical factor in chlorpropamide-alcohol flush." Eur J Clin Pharmacol 24 (1983): 237-42[21] Barnett AH, Spiliopoulos AJ, Pyke DA, et al "Metabolic studies in chlorpropamide-alcohol flush positive and negative type 2 (non-insulin dependent) diabetic patients with and without retinopathy." Diabetologia 24 (1983): 213-5[22] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[23] Jerntorp P, Almer LO "Chlorpropamide-alcohol flushing in relation to macroangiopathy and peripheral neuropathy in non-insulin dependent diabetes." Acta Med Scand 656 (1981): 33-6[24] "Product Information. Glucotrol (glipizide)." Pfizer US Pharmaceuticals, New York, NY.[25] "Position Statement: evidence-based nutrition principles and recommendations for the treatment and prevention of diabetes related complications. American Diabetes Association." Diabetes Care 25(Suppl 1) (2002): S50-S60[26] Hartling SG, Faber OK, Wegmann ML, Wahlin-Boll E, Melander A "Interaction of ethanol and glipizide in humans." Diabetes Care 10 (1987): 683-6', 1767365206);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (101114, 'Aminophylline', 'Metoprolol', 'Major', 'The pharmacologic effects of theophyllines and beta-blockers are opposite. Nonselective and high doses of cardioselective beta-blockers may cause severe or fatal bronchospasm by opposing theophylline-induced bronchodilation. Ophthalmic beta-blockers undergo significant systemic absorption and may also interact. In addition, propranolol and other beta-blockers may reduce the CYP450 hepatic metabolism of theophylline, and serum theophylline levels may be increased.', NULL, 'Oral and ophthalmic nonselective beta-blockers (e.', NULL, 'Oral and ophthalmic nonselective beta-blockers (e.g., carteolol, carvedilol, levobunolol, metipranolol, nadolol, oxprenolol, penbutolol, pindolol, propranolol, sotalol, and timolol) are considered contraindicated in patients with bronchospastic diseases. Cardioselective beta-blockers should generally be avoided, or used with extreme caution if no other alternatives are available and the benefits outweigh the risks of potentially severe bronchospasm. If patients do receive this combination, they should be closely monitored for increased serum theophylline levels but decreased bronchodilatory effectiveness.', 'Antagonism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/101228/', '[1] Upton RA "Pharmacokinetic interactions between theophylline and other medication (Part II)." Clin Pharmacokinet 20 (1991): 135-50[2] Mue S, Sasaki T, Shibahara S, et alT "Influence of metoprolol on hemodynamics and respiratory function in asthmatic patients." Int J Clin Pharmacol Biopharm 17 (1979): 346-50[3] Lombardi TP, Bertino JS, Goldberg A, Middleton E, Slaughter RL "The effects of a beta-2 selective adrenergic agonist and a beta- nonselective antagonist on theophylline clearance." J Clin Pharmacol 27 (1987): 523-9[4] Horvath JS, Woolcock AJ, Tiller DJ, Donnelly P, Armstrong J, Caterson R "A comparison of metoprolol and propranolol on blood pressure and respiratory function in patients with hypertension." Aust N Z J Med 8 (1978): 1-6[5] Conrad KA, Nyman DW "Effects of metoprolol and propranolol on theophylline elimination." Clin Pharmacol Ther 28 (1980): 463-7', 1767365206);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (101115, 'Atracurium', 'Metoprolol', 'Moderate', 'Neuromuscular blocking agents and beta-blockers may have additive cardiovascular depressant effects. Limited data suggest that beta-blockers may modestly potentiate the pharmacologic effects of neuromuscular blocking agents. The exact mechanism of interaction is unknown but may involve effects in the postsynaptic membrane, as beta-blockers alone have been reported to exacerbate or unmask myasthenia gravis.', NULL, 'Clinicians should recognize the potential for altered effects of neuromuscular blocking agents in the presence of beta-blockers.', NULL, 'Clinicians should recognize the potential for altered effects of neuromuscular blocking agents in the presence of beta-blockers. Respiratory and cardiovascular status should be closely monitored.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/101229/', '[1] Loan PB, Connolly FM, Mirakhur RK, Kumar N, Farling P "Neuromuscular effects of rocuronium in patients receiving beta-adrenoreceptor blocking, calcium entry blocking and anticonvulsant drugs." Br J Anaesth 78 (1997): 90-1[2] Harrah MD, Way WL, Katzung BG "The interaction of d-tubocurarine with antiarrhythmic drugs." Anesthesiology 33 (1970): 406-10[3] Rozen MS, Whan FM "Prolonged curarization associated with propranolol." Med J Aust 1 (1972): 467-8[4] Murthy VS, Patel KD, Elangovan RG, Hwang TF, Solochek SM, Steck JD, Laddu AR "Cardiovascular and neuromuscular effects of esmolol during induction of anesthesia." J Clin Pharmacol 26 (1986): 351-7[5] Glynne GL "Drug interaction?" Anaesthesia 39 (1984): 293[6] Herishanu Y, Rosenberg P "Beta-blockers and myasthenia gravis." Ann Intern Med 83 (1975): 834-5[7] Yate B, Mostafa SM "Drug interaction?" Anaesthesia 39 (1984): 728-9[8] Chapple DJ, Clark JS, Hughes R "Interaction between atracurium and drugs used in anaesthesia." Br J Anaesth 55 Suppl 1 (1983): s17-22', 1767365206);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (101116, 'Chloral hydrate', 'Metoprolol', 'Moderate', 'Many psychotherapeutic and CNS-active agents (e.g., anxiolytics, sedatives, hypnotics, antidepressants, antipsychotics, opioids, alcohol, muscle relaxants) exhibit hypotensive effects, especially during initiation of therapy and dose escalation. Coadministration with antihypertensives and other hypotensive agents, in particular vasodilators and alpha-blockers, may result in additive effects on blood pressure and orthostasis.', NULL, 'Caution and close monitoring for development of hypotension is advised during coadministration of these agents.', NULL, 'Caution and close monitoring for development of hypotension is advised during coadministration of these agents. Avoid alcohol in patients receiving vasodilating antihypertensive drugs. Patients should be advised to avoid rising abruptly from a sitting or recumbent position and to notify their physician if they experience dizziness, lightheadedness, syncope, orthostasis, or tachycardia.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/101230/', '[1] Feder R "Bradycardia and syncope induced by fluoxetine." J Clin Psychiatry 52 (1991): 139[2] Ellison JM, Milofsky JE, Ely E "Fluoxetine-induced bradycardia and syncope in two patients." J Clin Psychiatry 51 (1990): 385-6[3] Sternbach H "Fluoxetine-associated potentiation of calcium-channel blockers." J Clin Psychopharmacol 11 (1991): 390-1[4] Pacher P, Kecskemeti V "Cardiovascular side effects of new antidepressants and antipsychotics: new drugs, old concerns?" Curr Pharm Des 10 (2004): 2463-75[5] Cerner Multum, Inc. "Australian Product Information." O 0[6] Andrews C, Pinner G "Postural hypotension induced by paroxetine." BMJ 316 (1998): 595[7] Rodriguez de la Torre B, Dreher J, Malevany I, et al. "Serum levels and cardiovascular effects of tricyclic antidepressants and selective serotonin reuptake inhibitors in depressed patients." Ther Drug Monit 23 (2001): 435-40[8] Warrington SJ, Ankier SI, Turner P "Evaluation of possible interactions between ethanol and trazodone or amitriptyline." Neuropsychobiology 15 (1986): 31-7[9] "Product Information. Rexulti (brexpiprazole)." Otsuka American Pharmaceuticals Inc, Rockville, MD.[10] Gilman AG, Rall TW, Nies AS, Taylor P, eds. "Goodman and Gilman''s the Pharmacological Basis of Therapeutics. 8th ed." New York, NY: Pergamon Press Inc. (1990):[11] "Product Information. Fycompa (perampanel)." Eisai Inc, Teaneck, NJ.', 1767365206);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (101117, 'Chlorpromazine', 'Metoprolol', 'Moderate', 'Phenothiazines and neuroleptic agents may potentiate the hypotensive effect of some medications secondary to their peripheral alpha-1 adrenergic blocking activity. Orthostatic hypotension and syncope associated with vasodilation may occur, particularly during initial dosing and/or parenteral administration of the phenothiazine or neuroleptic.', NULL, 'Close clinical monitoring for development of hypotension is recommended if phenothiazines or neuroleptic agents are used in patients receiving antihypertensive medications or vasodilators.', NULL, 'Close clinical monitoring for development of hypotension is recommended if phenothiazines or neuroleptic agents are used in patients receiving antihypertensive medications or vasodilators. A lower starting dosage and slower titration of the phenothiazine or neuroleptic may be appropriate, especially in the elderly. Patients should be advised to avoid rising abruptly from a sitting or recumbent position and to notify their physician if they experience dizziness, lightheadedness, syncope, orthostasis, or tachycardia. Patients should also avoid driving or operating hazardous machinery.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/101231/', '[1] "Product Information. Seroquel (quetiapine)." Zeneca Pharmaceuticals, Wilmington, DE.[2] "Product Information. Rexulti (brexpiprazole)." Otsuka American Pharmaceuticals Inc, Rockville, MD.[3] "Product Information. Clozaril (clozapine)." Novartis Pharmaceuticals, East Hanover, NJ.[4] "Product Information. Risperdal (risperidone)." Janssen Pharmaceutica, Titusville, NJ.[5] "Product Information. Abilify (aripiprazole)." Bristol-Myers Squibb, Princeton, NJ.[6] Markowitz JS, Wells BG, Carson WH "Interactions between antipsychotic and antihypertensive drugs." Ann Pharmacother 29 (1995): 603-9[7] Aronowitz JS, Chakos MH, Safferman AZ, Lieberman JA "Syncope associated with the combination of clozapine and enalapril." J Clin Psychopharmacol 14 (1994): 429-30[8] "Product Information. Zyprexa (olanzapine)." Lilly, Eli and Company, Indianapolis, IN.[9] Fruncillo R, Gibbons W, Vlasses P, Ferguson R "Severe hypotension associated with concurrent clonidine and antipsychotic medication." Am J Psychiatry 142 (1985): 274[10] "Product Information. Geodon (ziprasidone)." Pfizer US Pharmaceuticals, New York, NY.[11] White WB "Hypotension with postural syncope secondary to the combination of chlorpromazine and captopril." Arch Intern Med 146 (1986): 1833-4', 1767365206);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (101118, 'Digitoxin', 'Metoprolol', 'Moderate', 'Concomitant use of digitalis glycosides and beta-blockers may increase the risk of bradycardia. These agents slow atrioventricular conduction and decrease heart rate, hence they may have additive cardiac effects during coadministration. Some beta-blockers such as carvedilol, esmolol, and talinolol have also been reported to increase the systemic bioavailability of digoxin. The mechanism may involve enhanced absorption as well as reduced renal excretion of digoxin due to inhibition of intestinal and renal P-glycoprotein efflux transporter.', NULL, 'Caution is advised during coadministration of digitalis glycosides and beta-blockers.', NULL, 'Caution is advised during coadministration of digitalis glycosides and beta-blockers. Serum digitalis levels, heart rate, and blood pressure should be monitored closely, particularly during the first few weeks of concomitant therapy. Patients should be advised to notify their physician if they experience anorexia, nausea, visual changes, irregular heartbeat, slow pulse, dizziness, or syncope. Beta-blockers should not be used in patients with overt or decompensated congestive heart failure, as sympathetic stimulation may be a vital component in maintaining hemodynamic function in these patients and its inhibition by beta blockade may worsen the heart failure.', 'Excretion', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/101232/', '[1] Lowenthal DT, Porter RS, Saris SD, Bies CM, Slegowski MB, Staudacher A "Clinical pharmacology, pharmacodynamics and interactions with esmolol." Am J Cardiol 56 (1985): f14-8[2] "Product Information. Zebeta (bisoprolol)." Lederle Laboratories, Wayne, NJ.[3] Watt DA "Sensitivity to propranolol after digoxin intoxication." Br Med J 2 (1968): 413-4[4] De Mey C, Brendel E, Enterling D "Carvedilol increases the systemic bioavailability of oral digoxin." Br J Clin Pharmacol 29 (1990): 486-90[5] Takara K, Kakumoto M, Tanigawara Y, Funakoshi J, Sakaeda T, Okumura K "Interaction of digoxin with antihypertensive drugs via MDR 1." Life Sci 70 (2002): 1491-1500[6] LeWinter MM, Crawford MH, O''Rourke RA, Karliner JS "The effects of oral propranolol, digoxin and combination therapy on the resting and exercise electrocardiogram." Am Heart J 93 (1977): 202-9[7] Wermeling DP, Field CJ, Smith DA, Chandler MH, Clifton GD, Boyle DA "Effects of long-term oral carvedilol on the steady-state pharmacokinetics of oral digoxin in patients with mild to moderate hypertension." Pharmacotherapy 14 (1994): 600-6[8] "Product Information. Coreg (carvedilol)." SmithKline Beecham, Philadelphia, PA.[9] Ratnapalan S, Griffiths K, Costei AM, Benson L, Koren G "Digoxin-carvedilol interactions in children." J Pediatr 142 (2003): 572-574[10] Eichhorn EJ, Lukas MA, Wu B, Shusterman N "Effect of concomitant digoxin and carvedilol therapy of mortality and morbidity in patients with chronic heart failure." Am J Cardiol 86 (2000): 1032-5[11] "Product Information. Inderal (propranolol)." Wyeth-Ayerst Laboratories, Philadelphia, PA.[12] Lowenthal DT, Porter RS, Achari R, Turlapaty P, Laddu AR, Matier WL "Esmolol-digoxin drug interaction" J Clin Pharmacol 27 (1987): 561-6', 1767365206);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (101119, 'Dobutamine', 'Metoprolol', 'Moderate', 'Beta-blockers may antagonize the cardiostimulatory effects of pressor agents by blocking beta-1 adrenergic receptors in the heart. Vasopressors may be less effective if the patient is receiving, or has recently received, a beta-blocking drug. In addition, peripheral vascular resistance may increase due to unopposed alpha-adrenergic effects of pressor agents in the presence of beta-blockade. Theoretically, the interaction may also occur with beta-blocker ophthalmic preparations, since they may be systemically absorbed and can produce clinically significant systemic effects even at low or undetectable plasma levels.', NULL, 'No specific intervention is necessary, but clinicians should be alert to the potential for diminished cardiac response when pressor agents are used in patients treated with beta-blockers, including ophthalmic formulations.', NULL, 'No specific intervention is necessary, but clinicians should be alert to the potential for diminished cardiac response when pressor agents are used in patients treated with beta-blockers, including ophthalmic formulations. A beta-blocker such as propranolol may be used to treat cardiac arrhythmias that occur during administration of pressor agents like mephentermine, metaraminol, norepinephrine, or phenylephrine.', 'Antagonism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/101233/', '[1] "Product Information. Dobutrex (dobutamine)." Lilly, Eli and Company, Indianapolis, IN.[2] "Product Information. Intropin (dopamine)." DuPont Pharmaceuticals, Wilmington, DE.[3] Richards DA, Prichard BN, Hernandez R "Circulatory effects of noradrenaline and adrenaline before and after labetalol." Br J Clin Pharmacol 7 (1979): 371-8', 1767365206);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (101120, 'Ergotamine', 'Metoprolol', 'Moderate', 'Several case reports have suggested that beta-blockers may potentiate the vasoconstrictive action of ergot alkaloids. The exact mechanism of interaction is unknown, but may involve blockade of beta-2-mediated (i.e., sympathetic) vasodilatation. In addition, beta-1 blockade reduces cardiac output, which can diminish blood flow and exacerbate ergot-induced vasospasm.', NULL, 'Except for increased awareness of the interaction, no specific intervention appears to be necessary.', NULL, 'Except for increased awareness of the interaction, no specific intervention appears to be necessary. Patients should be advised to seek immediate medical attention if they experience potential symptoms of ischemia such as coldness, pallor, cyanosis, numbness, tingling, or pain in the extremities; muscle weakness; severe or worsening headache; visual disturbances; severe abdominal pain; chest pain; and shortness of breath.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/101234/', '[1] "Product Information. Cafergot (caffeine-ergotamine)." Novartis Pharmaceuticals, East Hanover, NJ.[2] Venter CP, Joubert PH, Buys AC "Severe peripheral ischaemia during concomitant use of beta blockers and ergot alkaloids." Br Med J 289 (1984): 288-9[3] "Product Information. Migranal (dihydroergotamine nasal)." Novartis Pharmaceuticals, East Hanover, NJ.[4] Blank NK, Rieder MJ "Paradoxical response to propranolol in migraine." Lancet 2 (1973): 1336[5] Baumrucker JF "Drug interaction: propranolol and cafergot." N Engl J Med 288 (1973): 916-7[6] Gandy W "Dihydroergotamine interactio