INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (101206, 'Rifampicin', 'Losartan', 'Moderate', 'Administration of rifampin has been reported to result in a 35% decrease in losartan area under the curve (AUC) and 40% decrease in losartan''s metabolite E3174 (which has greater antihypertensive activity than the parent compound). The half life of both losartan and its active metabolite were decreased by 50%. A 44% increase in the oral clearance of losartan has also been reported. The mechanism may be due to rifampin''s induction of the CYP450 2C9 and 3A4 metabolism of losartan.', NULL, 'Blood pressure should be monitored carefully if rifampin is added to or withdrawn from the drug therapy of a patient receiving losartan.', NULL, 'Blood pressure should be monitored carefully if rifampin is added to or withdrawn from the drug therapy of a patient receiving losartan.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/101320/', '[1] Williamson KM, Patterson JH, McQueen RH, Adams KF, Pieper JA "Effects of erythromycin or rifampin on losartan pharmacokinetics in healthy volunteers." Clin Pharmacol Ther 63 (1998): 316-23[2] Ray K, Dorman S, Watson R "Severe hyperkalaemia due to the concomitant use of salt substitutes and ACE inhibitors in hypertension: a potentially life threatening interaction." J Hum Hypertens 13 (1999): 717-20[3] "Product Information. Cozaar (losartan)." Merck & Co, Inc, West Point, PA.[4] Zaidenstein R, Soback S, Gips M, Avni B, Dishi V, Weissgarten Y, Golik A, Scapa E "Effect of grapefruit juice on the pharmacokinetics of losartan and its active metabolite E3174 in healthy volunteers." Ther Drug Monit 23 (2001): 369-73', 17