INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (101254, 'Cabazitaxel', 'Lumacaftor', 'Moderate', 'Coadministration with potent inducers of CYP450 3A4 may significantly decrease the plasma concentrations of cabazitaxel, which is primarily metabolized by the isoenzyme. However, no formal drug interaction studies have been conducted.', NULL, 'Concomitant use of cabazitaxel with potent CYP450 3A4 inducers such as carbamazepine, enzalutamide, lumacaftor, mitotane, phenobarbital, phenytoin, primidone (partially metabolized to phenobarbital), rifamycins, and St.', NULL, 'Concomitant use of cabazitaxel with potent CYP450 3A4 inducers such as carbamazepine, enzalutamide, lumacaftor, mitotane, phenobarbital, phenytoin, primidone (partially metabolized to phenobarbital), rifamycins, and St. John''s wort should generally be avoided. The extent to which other, less potent CYP450 3A4 inducers may interact with cabazitaxel is unknown. Caution is advised if they are used with cabazitaxel.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/101368/', '[1] "Product Information. Jevtana (cabazitaxel)." sanofi-aventis , Bridgewater, NJ.[2] "Product Information. Jevtana (cabazitaxel)." sanofi-aventis , Bridgewater, NJ.[3] "Product Information. Trikafta (elexacaftor/ivacaftor/tezacaftor)." Vertex Pharmaceuticals, Cambridge, MA.[4] "Product Information. Kalydeco (ivacaftor)." Vertex Pharmaceuticals, Cambridge, MA.[5] "Product Information. Orkambi (ivacaftor-lumacaftor)." Vertex Pharmaceuticals, Cambridge, MA.[6] "Product Information. Symdeko 4-Week (ivacaftor-tezacaftor)." Vertex Pharmaceuticals, Cambridge, MA.', 1767365206);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (101255, 'Cabozantinib', 'Lumacaftor', 'Major', 'Coadministration with potent inducers of CYP450 3A4 may significantly decrease the plasma concentrations of cabozantinib, which is a substrate of the isoenzyme.', NULL, 'Chronic use of potent CYP450 3A4 inducers during treatment with cabozantinib should be avoided whenever possible.', NULL, 'Chronic use of potent CYP450 3A4 inducers during treatment with cabozantinib should be avoided whenever possible. If chronic coadministration is required, the daily dosage of cabozantinib should be increased in accordance with the prescribing information for the specific product. For the treatment of progressive, metastatic medullary thyroid cancer (capsule formulation), the recommendation is to increase cabozantinib dosage by 40 mg (e.g., from 140 mg to 180 mg daily or from 100 mg to 140 mg daily) as tolerated, not to exceed 180 mg/day.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/101369/', '[1] "Product Information. Cabometyx (cabozantinib)." Exelixis Inc, S San Francisco, CA.[2] "Product Information. Cometriq (cabozantinib)." Exelixis Inc, S San Francisco, CA.[3] "Product Information. Trikafta (elexacaftor/ivacaftor/tezacaftor)." Vertex Pharmaceuticals, Cambridge, MA.[4] "Product Information. Kalydeco (ivacaftor)." Vertex Pharmaceuticals, Cambridge, MA.[5] "Product Information. Orkambi (ivacaftor-lumacaftor)." Vertex Pharmaceuticals, Cambridge, MA.[6] "Product Information. Symdeko 4-Week (ivacaftor-tezacaftor)." Vertex Pharmaceuticals, Cambridge, MA.[7] "Product Information. Cometriq (cabozantinib)." Exelixis Inc, S San Francisco, CA.', 1767365206