INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (101278, 'Digoxin', 'Lumacaftor', 'Moderate', 'Based on in vitro data demonstrating both inhibition and induction of P-glycoprotein (P-gp) efflux transporter, lumacaftor has the potential to alter the serum concentrations of digoxin, a sensitive P-gp substrate. Additionally, a clinical study with ivacaftor monotherapy showed that ivacaftor is a weak inhibitor of P-gp. Therefore, concomitant use of lumacaftor/ivacaftor with P-gp substrates may alter the systemic exposure to these substrates.', NULL, 'Caution is advised when digoxin is used with lumacaftor/ivacaftor.', NULL, 'Caution is advised when digoxin is used with lumacaftor/ivacaftor. Serum digoxin levels and pharmacologic effects should be closely monitored and the dosage adjusted accordingly, particularly following initiation or discontinuation of lumacaftor/ivacaftor. Patients should be advised to notify their physician if they experience signs of digoxin toxicity such as nausea, anorexia, visual disturbances, slow pulse, or irregular heartbeats.', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/101392/', '[1] "Product Information. Orkambi (ivacaftor-lumacaftor)." Vertex Pharmaceuticals, Cambridge, MA.[2] "Product Information. Kalydeco (ivacaftor)." Vertex Pharmaceuticals, Cambridge, MA.[3] "Product Information. Trikafta (elexacaftor/ivacaftor/tezacaftor)." Vertex Pharmaceuticals, Cambridge, MA.[4] "Product Information. Kalydeco (ivacaftor)." Vertex Pharmaceuticals, Cambridge, MA.[5] "Product Information. Orkambi (ivacaftor-lumacaftor)." Vertex Pharmaceuticals, Cambridge, MA.[6] "Product Information. Symdeko 4-Week (ivacaftor-tezacaftor)." Vertex Pharmaceuticals, Cambridge, MA.[7] Darcy PF "Nutrient-drug interactions." Adverse Drug React Toxicol Rev 14 (1995): 233-54[8] Becquemont L, Verstuyft C, Kerb R, et al. "Effect of grapefruit juice on digoxin pharmacokinetics in humans." Clin Pharmacol Ther 70 (2001): 311-6', 1767365206);
