INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (101281, 'Doravirine', 'Lumacaftor', 'Major', 'Coadministration with potent inducers of CYP450 3A4 may significantly decrease the plasma concentrations of doravirine, which is primarily metabolized by the isoenzyme.', NULL, 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, concomitant use of doravirine with potent CYP450 3A4 inducers is considered contraindicated.', NULL, 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, concomitant use of doravirine with potent CYP450 3A4 inducers is considered contraindicated. At least a 4-week cessation period is recommended prior to initiation of doravirine treatment.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/101395/', '[1] "Product Information. Pifeltro (doravirine)." Merck & Company Inc, Whitehouse Station, NJ.[2] "Product Information. Pifeltro (doravirine)." Merck & Company Inc, Whitehouse Station, NJ.[3] "Product Information. Trikafta (elexacaftor/ivacaftor/tezacaftor)." Vertex Pharmaceuticals, Cambridge, MA.[4] "Product Information. Kalydeco (ivacaftor)." Vertex Pharmaceuticals, Cambridge, MA.[5] "Product Information. Orkambi (ivacaftor-lumacaftor)." Vertex Pharmaceuticals, Cambridge, MA.[6] "Product Information. Symdeko 4-Week (ivacaftor-tezacaftor)." Vertex Pharmaceuticals, Cambridge, MA.', 1767365206);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (101282, 'Doxorubicin (liposomal)', 'Lumacaftor', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of doxorubicin, which is primarily metabolized by the isoenzyme.', NULL, 'Concomitant use of doxorubicin with CYP450 3A4 inducers should generally be avoided, particularly potent ones like carbamazepine, enzalutamide, lumacaftor, mitotane, phenobarbital, phenytoin, primidone (partially metabolized to phenobarbital), rifamycins, and St.', NULL, 'Concomitant use of doxorubicin with CYP450 3A4 inducers should generally be avoided, particularly potent ones like carbamazepine, enzalutamide, lumacaftor, mitotane, phenobarbital, phenytoin, primidone (partially metabolized to phenobarbital), rifamycins, and St. John''s wort. Close monitoring for potentially reduced efficacy of doxorubicin is recommended if coadministration is required.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/101396/', '[1] "Product Information. Adriamycin PFS (doxorubicin)." Pharmacia and Upjohn, Kalamazoo, MI.[2] Riggs CE Jr, Engel S, Wesley M, Wiernik PH, Bachur NR "Doxorubicin pharmacokinetics: prochlorperazine and barbiturate effects." Clin Pharmacol Ther 31 (1982): 263[3] "Product Information. Doxil (doxorubicin liposomal)." Sequis Pharmaceuticals Inc, Menlo Park, CA.[4] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0', 1767365206);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (101283, 'Droperidol', 'Lumacaftor', 'Moderate', 'Theoretically, potent CYP450 3A4 inducers may decrease serum concentrations of butyrophenones such as benperidol or droperidol. This interaction has been reported for haloperidol. However, clinical data are lacking for benperidol or droperidol. In addition, butyrophenones may lower the seizure threshold. Central nervous system- and/or respiratory-depressant effects may also be additively or synergistically increased in patients taking droperidol or benperidol with certain other drugs that cause these effects, especially in elderly or debilitated patients.', NULL, 'The potential for diminished neuroleptic effects of droperidol or benperidol should be considered during coadministration with potent CYP450 3A4 inducers.', NULL, 'The potential for diminished neuroleptic effects of droperidol or benperidol should be considered during coadministration with potent CYP450 3A4 inducers. Pharmacologic response to these medications should be monitored more closely whenever a CYP450 3A4 inducer is added to or withdrawn from therapy, and the droperidol or benperidol dosage adjusted as necessary. Patients taking potent CYP450 3A4 inducers that are anticonvulsants should be monitored for loss of seizure control. Additionally, when droperidol or benperidol are used in combination with other drugs that cause CNS and/or respiratory depression, patients should be monitored for potentially excessive or prolonged CNS and respiratory depression. Ambulatory patients should be counseled to avoid hazardous activities requiring mental alertness and motor coordination until they know how these agents affect them, and to notify their doctor if they experience seizures or excessive CNS effects that interfere with their normal activities.', 'Metabolism, Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/101397/', '[1] Agencia Espaola de Medicamentos y Productos Sanitarios Healthcare "Centro de informacin online de medicamentos de la AEMPS - CIMA. Available from: URL: https://cima.aemps.es/cima/publico/home.html." ([2018]):[2] "Product Information. Rifadin (rifampin)." Hoechst Marion-Roussel Inc, Kansas City, MO.[3] "Product Information. Haldol (haloperidol)." McNeil Pharmaceutical, Raritan, NJ.[4] Arana GW, Goff DC, Friedman H, et al. "Does carbamazepine-induced reduction of plasma haloperidol levels worsen psychotic symptoms?" Am J Psychiatry 143 (1986): 650-1[5] Kim YH, Cha IJ, Shim JC, Shin JG, Yoon YR, Kim YK, Kim JI, Park GH, Jang IJ, Woo JI, Shin SG "Effect of rifampin on the plasma concentration and the clinical effect of haloperidol concomitantly administered to schizophrenic patients." J Clin Psychopharmacol 16 (1996): 247-52[6] Kidron R, Averbuch I, Klein E, Belmaker RH "Carbamazepine-induced reduction of blood levels of haloperidol in chronic schizophrenia." Biol Psychiatry 20 (1985): 219-22[7] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[8] Cerner Multum, Inc. "Australian Product Information." O 0[9] Kahn EM, Schulz SC, Perel JM, Alexander JE "Change in haloperidol level due to carbamazepine--a complicating factor in combined medication for schizophrenia." J Clin Psychopharmacol 10 (1990): 54-7[10] Takeda M, Nishinuma K, Yamashita S, et al "Serum haloperidol levels of schizophrenics receiving treatment for tuberculosis." Clin Neuropharmacol 9 (1986): 386-97[11] Jann MW, Ereshefsky L, Saklad SR, et al. "Effects of carbamazepine on plasma halopeidol levels." J Clin Pharmacol 5 (1985): 106-9[12] "Product Information. Trikafta (elexacaftor/ivacaftor/tezacaftor)." Vertex Pharmaceuticals, Cambridge, MA.[13] "