INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (101395, 'Propofol', 'Lumacaftor', 'Moderate', 'Coadministration with lumacaftor may decrease the plasma concentrations and therapeutic efficacy of drugs that are substrates of CYP450 3A4, 2B6, 2C19, 2C8, and/or 2C9. Lumacaftor is a potent inducer of CYP450 3A4 in vivo. Coadministration of lumacaftor with ivacaftor, a sensitive CYP450 3A4 substrate, decreased ivacaftor systemic exposure (AUC) by approximately 80%.', NULL, 'Caution is advised when lumacaftor/ivacaftor is prescribed with drugs that undergo metabolism by CYP450 3A4, 2B6, 2C19, 2C8 and/or 2C9, particularly those with a narrow therapeutic range.', NULL, 'Caution is advised when lumacaftor/ivacaftor is prescribed with drugs that undergo metabolism by CYP450 3A4, 2B6, 2C19, 2C8 and/or 2C9, particularly those with a narrow therapeutic range.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/101509/', '[1] "Product Information. Orkambi (ivacaftor-lumacaftor)." Vertex Pharmaceuticals, Cambridge, MA.[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] Cerner Multum, Inc. "Canadian Product Information." O 0 (2015):[4] Cerner Multum, Inc. "Australian Product Information." O 0[5] "Product Information. Kalydeco (ivacaftor)." Vertex Pharmaceuticals, Cambridge, MA.[6] "Product Information. Orkambi (ivacaftor-lumacaftor)." Vertex Pharmaceuticals, Cambridge, MA.[7] "Product Information. Accolate (zafirlukast)." Zeneca Pharmaceuticals, Wilmington, DE.[8] "Product Information. Trikafta (elexacaftor/ivacaftor/tezacaftor)." Vertex Pharmaceuticals, Cambridge, MA.[9] "Product Information. Kalydeco (ivacaftor)." Vertex Pharmaceuticals, Cambridge, MA.[10] "Product Information. Orkambi (ivacaftor-lumacaftor)." Vertex Pharmaceuticals, Cambridge, MA.[11] "Product Information. Symdeko 4-Week (ivacaftor-tezacaftor)." Vertex Pharmaceuticals, Cambridge, MA.', 1767365206);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (101396, 'Quetiapine', 'Lumacaftor', 'Major', 'Coadministration with potent inducers of CYP450 3A4 may significantly decrease the plasma concentrations of quetiapine, which is primarily metabolized by the isoenzyme. The interaction has also been reported with phenytoin, another potent CYP450 3A4 inducer.', NULL, 'Increased dosages of quetiapine may be required during chronic treatment (greater than 7 to 14 days) with potent CYP450 3A4 inducers.', NULL, 'Increased dosages of quetiapine may be required during chronic treatment (greater than 7 to 14 days) with potent CYP450 3A4 inducers. The manufacturer recommends an increase of up to 5-fold the original dosage of quetiapine. Further adjustments should be made based on clinical response and tolerance. Continued treatment at higher dosages should only be considered following careful consideration of risks and benefits. When the CYP450 3A4 inducer is discontinued, the dosage of quetiapine should be reduced to the original level within 7 to 14 days.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/101510/', '[1] "Quetiapine for schizophrenia." Med Lett Drugs Ther 39 (1997): 117-8[2] "Product Information. Seroquel (quetiapine)." Zeneca Pharmaceuticals, Wilmington, DE.[3] Wong YWJ, Yeh C, Thyrum PT "The effects of concomitant phenytoin administration on the steady-state pharmacokinetics of quetiapine." J Clin Psychopharmacol 21 (2001): 89-93[4] Cerner Multum, Inc. "Australian Product Information." O 0[5] Grimm SW, Richtand NM, Winter HR, Stams KR, Reele SB "Effects of cytochrome P450 3A modulators ketoconazole and carbamazepine on quetiapine pharmacokinetics." Br J Clin Pharmacol 61 (2006): 58-69[6] Patel S, Robinson R, Burk M "Hypertensive crisis associated with St. John''s Wort." Am J Med 112 (2002): 507-8', 1767365206);
