INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (101450, 'Vemurafenib', 'Lumacaftor', 'Major', 'Coadministration with potent inducers of CYP450 3A4 may decrease the plasma concentrations of vemurafenib, which has been shown in vitro to be a substrate of the isoenzyme.', NULL, 'Concomitant use of vemurafenib with potent CYP450 3A4 inducers should generally be avoided.', NULL, 'Concomitant use of vemurafenib with potent CYP450 3A4 inducers should generally be avoided. Alternative therapeutic agents with less enzyme induction potential should be considered whenever possible during treatment with vemurafenib. If coadministration is required, the manufacturer recommends increasing the dose of vemurafenib by 240 mg (one tablet) as tolerated. Close monitoring for toxicities (e.g., QT prolongation, hepatotoxicity, nephrotoxicity, photosensitivity, dermatologic reactions, uveitis) is advised following dose increase. Two weeks after discontinuation of the strong CYP450 3A4 inducer, the vemurafenib dose that was taken prior to initiating the inducer may be resumed.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/101564/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] "Product Information. Zelboraf (vemurafenib)." Genentech, South San Francisco, CA.[4] Patel S, Robinson R, Burk M "Hypertensive crisis associated with St. John''s Wort." Am J Med 112 (2002): 507-8', 1767365206);
