INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (101483, 'Flibanserin', 'Pregabalin', 'Moderate', 'The central nervous system (CNS) effects of flibanserin such as somnolence and fatigue may be potentiated by concomitant use of other agents with CNS depressant effects. The risk of CNS depression is increased if flibanserin is taken during waking hours or with alcohol or other CNS depressants.', NULL, 'Patients receiving flibanserin with other agents that can cause CNS depression should be apprised of the potential for increased adverse effects such as drowsiness, dizziness, lightheadedness, and fatigue.', NULL, 'Patients receiving flibanserin with other agents that can cause CNS depression should be apprised of the potential for increased adverse effects such as drowsiness, dizziness, lightheadedness, and fatigue. Patients should not drive, operate machinery, or engage in hazardous activities requiring mental alertness and motor coordination for at least 6 hours after taking flibanserin.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/101597/', '[1] "Product Information. Addyi (flibanserin)." Sprout Pharmaceuticals, Raleigh, NC.[2] "Product Information. Addyi (flibanserin)." Sprout Pharmaceuticals, Raleigh, NC.[3] Warrington SJ, Ankier SI, Turner P "Evaluation of possible interactions between ethanol and trazodone or amitriptyline." Neuropsychobiology 15 (1986): 31-7[4] "Product Information. Fycompa (perampanel)." Eisai Inc, Teaneck, NJ.[5] Gilman AG, Rall TW, Nies AS, Taylor P, eds. "Goodman and Gilman''s the Pharmacological Basis of Therapeutics. 8th ed." New York, NY: Pergamon Press Inc. (1990):[6] "Product Information. Rexulti (brexpiprazole)." Otsuka American Pharmaceuticals Inc, Rockville, MD.', 1767365206);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (101484, 'Fluoxetine', 'Pregabalin', 'Moderate', 'The efficacy of anticonvulsants may be diminished during coadministration with selective serotonin reuptake inhibitors (SSRIs) or serotonin-norepinephrine reuptake inhibitor (SNRIs). Antidepressants including SSRIs and SNRIs can reduce seizure threshold.Coadministration of SSRIs or SNRIs with some anticonvulsants, particularly carbamazepine, eslicarbazepine, oxcarbazepine and valproic acid, may increase the risk of hyponatremia. The risk appears to be dose-related, and elderly patients and patients who are volume depleted (e.g., diuretic use) may be at greater risk.', NULL, 'SSRIs and SNRIs should be avoided in patients with unstable epilepsy, and used cautiously in patients with epilepsy controlled with anticonvulsant medications.', NULL, 'SSRIs and SNRIs should be avoided in patients with unstable epilepsy, and used cautiously in patients with epilepsy controlled with anticonvulsant medications. Treatment with SSRIs and SNRIs should be discontinued if seizures develop or seizure frequency increases. Patients receiving SSRIs or SNRIs with anticonvulsants, particularly carbamazepine, eslicarbazepine, oxcarbazepine and/or valproic acid, should also have serum sodium levels measured regularly and monitored for development of hyponatremia, particularly when higher dosages of these medications are used. Signs and symptoms of hyponatremia include nausea, vomiting, headache, difficulty concentrating, memory impairment, confusion, malaise, lethargy, muscle weakness or spasms, and unsteadiness. In more severe and/or acute cases, hallucination, syncope, seizure, coma, respiratory arrest, and death may occur.', 'Antagonism