    ) VALUES (1384638, 'Aminoglutethimide', 'Ceritinib', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of ceritinib, which is a substrate of the isoenzyme.', NULL, 'Patients receiving ceritinib with CYP450 3A4 inducers should be monitored for potentially diminished pharmacologic response to ceritinib.', NULL, 'Patients receiving ceritinib with CYP450 3A4 inducers should be monitored for potentially diminished pharmacologic response to ceritinib. Concomitant use should be avoided if an interaction is suspected.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
