INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (102030, 'Varicella Zoster Vaccine (Recombinant)', 'Valaciclovir', 'Major', 'Concomitant use of live, attenuated varicella virus vaccine or zoster vaccine with acyclovir, famciclovir, or valacyclovir may result in diminished immunologic response to the vaccine. Although clinical data are lacking, theoretical concern exists that antiviral activity against the vaccine strain may prevent a sufficient degree of replication to occur in order to induce a protective immune response. Acyclovir, specifically, has demonstrated activity against the Oka strain of live, attenuated varicella-zoster virus used in the vaccines.', NULL, 'Acyclovir, famciclovir, and valacyclovir should not be used for at least 24 hours before administration of live, attenuated varicella virus vaccine or zoster vaccine.', NULL, 'Acyclovir, famciclovir, and valacyclovir should not be used for at least 24 hours before administration of live, attenuated varicella virus vaccine or zoster vaccine. In addition, these antiviral agents should generally be avoided for 14 days after vaccination.', 'Antagonism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/102148/', '[1] "Product Information. Zovirax (acyclovir)." Mylan Pharmaceuticals Inc, Morgantown, WV.[2] US Centers for Disease Control and Prevention (CDC) "Recommended immunization schedule for adults aged 19 years or older, United States, 2017 Available from: URL: https://www.cdc.gov/vaccines/schedules/downloads/adult/adult-combined-schedule-bw.pdf." ([2017, Feb]):', 1767363023);
