undice.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/102306/', '[1] "Product Information. Plegridy (peginterferon beta-1a)." Biogen Idec Inc, Cambridge, MA.[2] "Product Information. Betaseron (interferon beta-1b)." Berlex, Richmond, CA.[3] "Product Information. Rebif (interferon beta-1a)." Serono Laboratories Inc, Norwell, MA.[4] "Product Information. Avonex (interferon beta-1a)." Biogen, Cambridge, MA.', 1767363023);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (102188, 'Iron protein succinylate', 'Methyldopa', 'Moderate', 'INTERVAL: The oral bioavailability and pharmacologic effects of methyldopa may be decreased during concurrent administration with iron-containing products. The proposed mechanism is chelation of methyldopa by the iron cation, forming an insoluble complex that is poorly absorbed from the gastrointestinal tract.', NULL, 'Until more information is available, patients receiving methyldopa in combination with iron-containing products should be advised to separate the times of administration by as much as possible.', NULL, 'Until more information is available, patients receiving methyldopa in combination with iron-containing products should be advised to separate the times of administration by as much as possible. Patients should be monitored closely for altered hypertensive effect and methyldopa dosage increased as necessary. Selection of an alternative antihypertensive therapy may be necessary.', 'Absorption', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/102307/', '[1] Campbell N, Paddock V, Sundaram R "Alteration of methyldopa absorption, metabolism, and blood pressure control caused by ferrous sulfate and ferrous gluconate." Clin Pharmacol Ther 43 (1988): 381-6[2] Campbell NR, Campbell RR, Hasinoff BB "Ferrous sulfate reduces methyldopa absorption: methyldopa: iron complex formation as a likely mechanism." Clin Invest Med 6 (1990): 329-32[3] Campbell NR, Hasinoff BB "Iron supplements: a common cause of drug interactions." Br J Clin Pharmacol 31 (1991): 251-5', 1767363023);
